import { motion } from "framer-motion"
import estyle from "./MainNav.module.scss"
import { Link, useNavigate } from "react-router-dom"
import { useState, useContext, useEffect } from "react"
import NavContext from '../../NavContext'

function MainNav({props}) {
    const menudisabledamount = props.disablemenuamount * 1000
    const [enablebutton, setEnableButton] = useState(true)
    const [isPrevActive, setPrevActive] = useState('')
    const [isActive, setActive] = useState('about');
    const [isFirstTime, setIsFirstTime] = useState(true)
    const { changenav } = useContext(NavContext)
    const { submodules } = useContext(NavContext)
    const { changesubmodule } = useContext(NavContext)
    const navlabels = ["ABOUT", "PROJECTS", "CLIENTS", "PARTNERS", "FUTURE", "CONTACT"]
    const thenav = []
    const navigate = useNavigate()

    let amountoftimebeforegoingtourl = 2 * 1000

    //need to figure out how to animate the button color for the selected menu item, while keeping the menu unclickable for the menudisabledamount of time

    const handleHoverVariant = {
        fromselected: {color: '#03FFFA'}, /* teal */
        tounselected: { color:'#292728',  /* charcoal */ 
            transition: { 
                color:{ duration: props.hoveranimduration }
            }
        },
        hoveranim: {
            color:props.hovercolor, /* charcoal */ 
            transition:{ duration:props.hoveranimduration }
        }
    }

    const handleClick = (e, waittime, duration, label) => {

        if(isActive !== label)
        {
            setEnableButton(false)

            setTimeout(() => {        
                setEnableButton(true)
            }, menudisabledamount)

            setPrevActive(isActive)

            setActive(label);

            if(submodules.length === 0)
            {
                console.log('submodules is blank')
                navigatetourl(`/${e.target.childNodes[0].data.toLowerCase()}`, e.target.childNodes[0].data.toLowerCase(), waittime, duration)
            }else{

            }

            submodules.map((item) => {

                console.log('what is submodules.yesopen currently set to : ' + item.yesopen)

                if(item.yesopen === true)
                {
                    //close the menu
                    console.log('close the menu')
                    //then call the nav section to be loaded
                    console.log('then call the nav section to be loaded: ' + e.target.childNodes[0].data.toLowerCase())

                }
                else
                {
                    console.log('menu doesnt need to be closed just call the nav')
                    // setTimeout(() => {        
                    //     navigate(`/${e.target.childNodes[0].data.toLowerCase()}`);
                    //     changenav(e.target.childNodes[0].data.toLowerCase(), waittime, duration)
                    // }, 2000)
                    navigatetourl(`/${e.target.childNodes[0].data.toLowerCase()}`, e.target.childNodes[0].data.toLowerCase(), waittime, duration)
                }
            })


            // if(item.yesopen === true)
            // changesubmodule('', false)



            //goToPage(e.target.childNodes[0].data.toLowerCase())
            //changenav(e.target.childNodes[0].data.toLowerCase(), waittime, duration)



        }
    }

    const navigatetourl = (url, label, waittime, duration) => {
        setTimeout(() => {        
            navigate(url);
            changenav(label, waittime, duration)
        }, amountoftimebeforegoingtourl)
    }

    useEffect(() => {

        // console.log('useEffect - isPrevActive has changed to: ' + isPrevActive)
        // console.log('useEffect - isActive has changed to: ' + isActive)

        if(isFirstTime === true){
            console.log('firsttime is ' + isFirstTime)
            console.log('props.sectiondelay is: ' + props.sectiondelay)
            changenav('about', props.sectiondelay)
            setIsFirstTime(false)
        }
        else
        {
            console.log('useeffect - else -  ' + isFirstTime)
            console.log('firsttime is ' + isFirstTime)
            setTimeout(() => {        
                setPrevActive('')
            }, 3000)
        }
    }, [isPrevActive])

    console.log('firsttime is now: ' + isFirstTime)

    for(let i = 0; i < navlabels.length; i++) {
        thenav.push(
            <motion.li key={i} className={estyle.mainnavbtn}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay:props.delay + (i*.2), duration: props.duration }}
            >
                {enablebutton ?
                    <div className={`
                            ${estyle.link} ${navlabels[i].toLowerCase()} 
                            ${(isActive === navlabels[i].toLowerCase() ? null : estyle.selected)}`} 
                            // to={`/${navlabels[i]}`.toLowerCase()} 
                            onClick={(e) => handleClick(e, props.sectiondelay, props.sectionduration, navlabels[i].toLowerCase())}
                    >
                        {isPrevActive === navlabels[i].toLowerCase() ?
                        <motion.div 
                            className={estyle.link}
                            variants={handleHoverVariant}
                            initial="fromselected"
                            animate="tounselected"
                            // whileHover={isActive === navlabels[i].toLowerCase() ? "startanim" : "hoveranim"}
                            whileHover={"hoveranim"}
                        >
                        {navlabels[i]}
                        </motion.div> :
                        <motion.div
                            className={`${estyle.link} ${(isActive === navlabels[i].toLowerCase() ? estyle.selected : null)}`}
                            variants={handleHoverVariant}
                            whileHover={"hoveranim"}

                        >
                            {navlabels[i]}
                        </motion.div> 
                        }
                    </div> :
                    <div className={`${estyle.link} ${(isPrevActive === navlabels[i].toLowerCase() ? estyle.selected : null)} ${(isActive === navlabels[i].toLowerCase() ? estyle.selected : null )}`}>{navlabels[i]}</div>
                }
            </motion.li>
        )
    }

    return (
        <nav>
            <ul className={estyle.mainnav}>
                {thenav}
            </ul>
        </nav>
    )
}
export default MainNav;
