import { motion, useAnimate } from "framer-motion"
import estyle from "./SubModulePress.module.scss"

function SubModulePress({props}) {

    const amountofpressreleases = 5
    const pressreleases = []

    const subtitle = ["press release 1", "press release 2", "press release 3", "press release 4", "press release 5"]
    const subbody = ["body copy 1", "body copy 2", "body copy 3", "body copy 4", "body copy 5"]
    const button = "learn more"

    const svgVariants = {
        ready: { opacity: 0 },
        start: { opacity: 1, transition: { delay: props.delay, duration : props.duration }}
    }

    const handleClick = (e) => {
        //don't need anything here i guess
    }

    for(let i = 0; i < amountofpressreleases; i++){
        pressreleases.push(
            <div key={i} className={estyle.contentarea}>
                <div className={estyle.module2col1}>
                    <motion.div className={estyle.contentimage} id="contentimage" style={{opacity:1}}>
                    </motion.div>
                </div>
                <div className={estyle.module2col2}>
                    <motion.div className={estyle.contenttitle} id="contenttitle" style={{opacity:1}}>
                        {subtitle[i]}
                    </motion.div>
                    <motion.div className={estyle.contentbody} id="contentbody" style={{opacity:1}}>
                        {subbody[i]}
                    </motion.div>
                    <motion.div className={`${estyle.contentlink} ${estyle.contentlink2}`} id="contentlink" style={{opacity:1, y:0, x:0}} whileHover={{ color:"#03FFFA" }} onClick={() => {handleClick('press')}}>
                       {button}
                    </motion.div>
                </div>   
            </div> 
        )
    }

    return (
        <motion.div className={estyle.container}
            // ref={scope}
            variants={svgVariants}
            initial="ready"
            animate="start"
        >
            <div>sub module press stuff</div>
            {pressreleases}
        </motion.div>
    )
}
export default SubModulePress;
