import { motion, useAnimationControls } from "framer-motion"
import estyle from "./RectangleDot.module.scss"
import { useEffect } from "react"

function RectangleDot({props}) {

    useEffect(() => {

        const sequence = async () => {
            await divControls.start('fadein')
            await divControls.start('flash1')
            await divControls.start('flash2')
        }

        sequence()
    })

    const divControls = useAnimationControls();

    const rectangledotanim = {
        ready: { opacity: 0, scale:.5},
        fadein: { opacity:1, scale:1,
            transition: {
                opacity: { delay:props.delay, duration: .2 },
                scale: { delay: props.delay, duration: .4, type:'tween', ease:'backInOut' }
            }
        },

        flash1: { opacity: 0,
            transition: {
                outline: {delay: 0, duration: .05}
            }
        },
        flash2: { opacity: 1,
            transition: {
                outline: {delay: 0, duration: .05}
            }
        },
    }


    return (
        <div>
            {/* <motion.svg className={estyle.rectangledot} style={{width:50, height:props.height}} viewBox="0 0 50 21" fill="black" xmlns="http://www.w3.org/2000/svg" */}
            <motion.svg className={estyle.rectangledot} width="20" height="400" fill="black" xmlns="http://www.w3.org/2000/svg"
                variants={rectangledotanim}
                initial="ready"
                animate={divControls}
            >
                <path d="M20.9852 0.721436H0.985596V20.7211H20.9852V0.721436Z" fill={props.color}/>

            </motion.svg>
        </div>
    )
}
export default RectangleDot;
