import { motion } from "framer-motion"
import estyle from "./AngleLine.module.scss"

function AngleLine({props}) {

    const svgVariants = {
        hidden: { opacity: 0 },
        visible: { opacity: 1, transition: { delay: props.delay, duration : 1 }}
    }

    const startdot = {
        ready: { opacity:0, scale:0 },
        start: { opacity:1, scale:1, 
            transition: {
                scale:{ 
                    delay: props.dot1delay, 
                    duration: props.dot1duration, 
                    type:'spring', 
                    bounce:1, 
                    velocity:1, 
                    stiffness:200, 
                    mass:1, 
                    damping:8 
                },
            }}
    }

    const enddot = {
        ready: { opacity:0, scale:0 },
        start: { opacity:1, scale:1, 
            transition: {
                scale:{ 
                    delay: props.dot2delay, 
                    duration: props.dot2duration, 
                    type:'spring', 
                    bounce:1, 
                    velocity:1, 
                    stiffness:200, 
                    mass:1, 
                    damping:8 
                },
            }}
    }

    const startline = {
        ready: { y:0, x: -900 },
        start: { x: 0, 
            transition: { 
                duration: props.lineduration, 
                delay: props.linedelay, 
                type:'tween', 
                ease:'easeOut' 
            }    
        }
    }

    return (
        <div>
            <motion.svg  width="500" height="200" viewBox="0 0 500 200" fill="none" xmlns="http://www.w3.org/2000/svg"
                variants={svgVariants}
                initial="hidden"
                animate="visible"
            >
                <defs>
                    <clipPath id="myclippath">
                    <motion.rect 
                        height="200" 
                        width="500"
                        variants={startline}
                        initial="ready"
                        animate="start"
                    />
                    </clipPath>
                </defs>

                <g id="Frame 1">
                    <motion.circle id="startdot" cx="27" cy="100" r="5" fill={props.color} 
                        variants={startdot}
                        initial="ready"
                        animate="start" 
                    />
                    <path clipPath="url(#myclippath)" id="startline" d="M27 100.5H335" strokeWidth="1" stroke={props.color}/>
                    <path clipPath="url(#myclippath)" id="angleline" d="M334.609 99.6877L366.609 59.6877" stroke={props.color}/>
                    <path clipPath="url(#myclippath)" id="endline" d="M466 59.5L366 59.5" stroke={props.color}/>
                    <motion.circle id="enddot" cx="467" cy="59" r="5" fill={props.color}
                        variants={enddot}
                        initial="ready"
                        animate="start"
                    />
                </g>
            </motion.svg>
        </div>
    )
}
export default AngleLine;
