import { motion } from "framer-motion"
import estyle from "./MainNav.module.scss"
import { Link } from "react-router-dom"
import { useState, useEffect, useContext } from "react"
import NavContext from '../../NavContext'

function MainNav({props}) {

    //need to disable the menu until a section is completely 'loaded in'

    const menudisabledamount = props.disablemenuamount * 1000
    const [enablebutton, setEnableButton] = useState(true)
    const [isactive, setIsActive] = useState(true); 
    const { changenav } = useContext(NavContext)
    const navlabels = ["ABOUT", "PROJECTS", "CLIENTS", "PARTNERS", "FUTURE", "CONTACT"]
    const thenav = []

    const handleClick = (e, waittime, navitem) => {
        setEnableButton(false)
        setTimeout(() => {
            setEnableButton(true)

        }, menudisabledamount)

        setIsActive(true)

        changenav(e.target.childNodes[0].data.toLowerCase(), waittime)
    }

    useEffect(() => {
        if(enablebutton == true)
        {
            console.log(enablebutton)
            console.log('its been enabled again')

            // setIsActive(-1)
        } 
        else if(enablebutton == false)
        {
            console.log(enablebutton)
            console.log('its not enabled')
        }
    })

    for(let i = 0; i < navlabels.length; i++) {
        thenav.push(
            <motion.li key={i} className={estyle.mainnavbtn}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay:props.delay + (i*.2), duration: props.duration }}
            >
                    {enablebutton ?
                        <Link key={i} className={`${estyle.link} ${key ? estyle.speciallink : ""} ${navlabels[i].toLowerCase()}`} to={`/${navlabels[i]}`.toLowerCase()} onClick={(e) => handleClick(e, props.sectiondelay, navlabels[i])}>
                        <motion.div
                            whileHover={{ color:props.hovercolor }}
                            transition={{ duration:props.hoveranimduration}}
                        >
                            {navlabels[i]}
                        </motion.div>
                    </Link> :
                    <div className={estyle.nolink} to={`/${navlabels[i]}`.toLowerCase()}>{navlabels[i]}</div>
                    }
            </motion.li>
        )
    }

    return (
        <nav>
            <ul className={estyle.mainnav}>
                {thenav}
            </ul>
        </nav>
    )
}
export default MainNav;
