import { createContext, useState } from "react";

const NavContext = createContext();

export function NavProvider({children}){

    const [currentnav, setNav] = useState({ navname: 'default', waittime: 0 })

    let thenavname:string = ''
    let thewaittime:number = 0

    const changenav = (navname, waittime) => {
        console.log('---- check navname and waittime parameters -----')
        console.log('typeof navname parameter is: ' + typeof(navname))
        console.log('typeof waittime parameter is: ' + typeof(waittime))
        console.log('navnmae is: ' + navname)
        console.log('waittime is: ' + waittime)

        console.log('---- check thenavname and thewaittime parameters -----')
        console.log('typeof thenavname string is: ' + typeof(thenavname))
        console.log('typeof thewaittime string is: ' + typeof(thewaittime))
        console.log('thenavname is: ' + thenavname)
        console.log('thewaittime is: ' + thewaittime)

        console.log('---- check currentnav.navname and currentnav.waittime parameters -----')
        console.log('typeof navname parameter is: ' + typeof(currentnav.navname))
        console.log('typeof waittime parameter is: ' + typeof(currentnav.waittime))
        console.log('navnmae is: ' + currentnav.navname)
        console.log('waittime is: ' + currentnav.waittime)

        console.log('setnav is being set to be thenavname and thewaittime now...')
        setNav(currentnav.navname === navname, currentnav.waittime === waittime)
        console.log('typeof thenavname is: ' + typeof(thenavname))
        console.log('typeof thewaittime is: ' + typeof(thewaittime))

        console.log('typeof thenavname after it is used in state is: ')

        // console.log('navname is: ' + navname)
        // console.log('waittime is: ' + waittime)
        // console.log('changenav - navname is: ' + navname)
        // console.log('changenav - waittime is: ' + waittime)
        // console.log('setnav.navname is: ' + currentnav.navname)
        // console.log('setnav.waittime is: ' + currentnav.waittime)
        // console.log('currentnav is: ' + currentnav)
    }
    return(
        <NavContext.Provider value={{currentnav, changenav}}>
            {children}
        </NavContext.Provider>
    )
}
export default NavContext;


//nav context is like redux but in the opposite, you import this into whatever other component
//you want to use this data.

//right now the main nav calls changenav and sets the navname to whatever was clicked, and
//also sets the waittime to whatever is set in mainnav

//then the other 'sections' (components like about, projects etc) also import this component
//and call the currentnav state to find out what it currently is, based off this they should
//be able to wait for however long the waittime is until they animate in. that's the plan.
