import { motion, useAnimate } from "framer-motion"
import estyle from "./About.module.scss"
import NavContext from "../../NavContext"
import { useContext, useEffect, useState } from "react"

function About({props}) {



    const [showSection0, setShowSection0] = useState(true)
    const [showSection1, setShowSection1] = useState(false)
    const [showSection2, setShowSection2] = useState(false)
    const [menupos, setMenuPos] = useState(0)

    let whichsection = []



    //the mapping has to happen before the variants animation so that
    //it can set 'thedelay' number before the variant animation uses it

    /*

        title:"RECOGNITION",
        subtitle:"Meticulously Designed",
        subbody:"[company] has been meticulously designed and trained to tackle all challenges with an extraordinary blend of wit...",
        button: "view all"


    */

    let pretitle = 'THE'
    let title = 'COMPANY'
    let menu = ['WHO WE ARE', 'OUR APPROACH', 'OUR VISION']
    let bodycopy0 = "We are a leading artificial intelligence technology \ncompany, dedicated to helping businesses and \norganizations harness the power of AI to improve their\noperations and drive their success. Our team of\nexperienced AI engineers and researchers are\npassionate about developing innovative solutions that \ncan enable businesses to leverage the potential of \nAI to increase efficiency,"
    let bodycopy1 = "When designing and developing artificial \nintelligence, companies no longer have to \nfocus solely on the nitty-gritty technical \ndetails. Instead, a holistic approach is \nneeded to ensure success. Additionally, they \nmust ensure that all laws and regulations \nrelating to data privacy and technology are \nadhered to and must focus on protecting users \nfrom unintended consequences. At Atomi, we do \nall that and more. Contact us to find out \nwhat exactly that ‘more’ is."
    let bodycopy2 = "Atomi’s vision for artificial intelligence can guide future strategies and support long-term growth. \nWe believe the current and future potential of AI should be leveraged to drive meaningful progress, \nnot only for our organization but for society and humanity as a whole. \nWe strive to lead the industry in AI-driven solutions while delivering end-user benefits such as convenience, flexibility, and security across our product lines"


    let thedelay = 0;
    let theduration = 0;

    const [scope, animate] = useAnimate();
    const { currentnav } = useContext(NavContext)


    currentnav.map((navitem) => {
        if(navitem.navname === "about"){
            console.log('about - navitem navname is:' + navitem.navname)
            console.log('about - navitem waittime is:' + navitem.waittime)
            console.log('about - navitem duration is:' + navitem.duration)
        }
        thedelay = navitem.waittime
        theduration = navitem.duration
        console.log('about - thedelay is : ' + thedelay)
    })

    const svgVariants = {
        ready: { opacity:0 },
        start: { opacity:1, 
            transition: { 
                // scaleY: { delay: 2.35, duration: .25, type:'tween', ease:'easeIn' },
                // y: { delay:2.35, duration: .25, type: 'tween', ease:'easeIn' },
                // scaleX: { delay: 2.6, duration: .25, type:'tween', ease:'backOut' }
                //delay has to be the same or greater than the exit duration for other sections
                opacity: { delay:thedelay, duration:theduration }
            }
        },
        exit: {opacity:0, transition: {opacity: {duration:theduration}}}
    }

    const backgroundanim = {
        ready: { opacity:0 },
        start: { opacity:1,
            transition: {
                opacity: {delay:thedelay, duration:theduration}
            }
        }
    }

    const nextanim = async (delay) => { 
        // console.log('section clicked? ' + )
        //console.log('this is the next animation')
        await animate('#bg', { opacity:1, x:0})
        //await animate('#title', { opacity:1 }, {delay:delay})
    }



    const handleClick = (e, label, num) => {

        setMenuPos(num)
        console.log('menupos is: ' + num)

        switch(num) {

            case 0:
                setShowSection0(true)
                setShowSection1(false)
                setShowSection2(false)
                renderSection()
                break;
            case 1:
                setShowSection0(false)
                setShowSection1(true)
                setShowSection2(false)
                renderSection()
                break;
            case 2:
                setShowSection0(false)
                setShowSection1(false)
                setShowSection2(true)
                renderSection()
                break;
                default:
        }
    }


    const renderSection = () => {
        whichsection = []
        if(showSection0 === true) {
            console.log('should be bodycopy 0')
            whichsection.push
            (
                <p className={estyle.cssfix}>{bodycopy0}</p>
            )
        }
        else if(showSection1 === true){

            console.log('should be bodycopy 1')
            whichsection.push
            (
                <p className={estyle.cssfix}>{bodycopy1}</p>
            )
        }
        else if(showSection2 === true){

            console.log('should be bodycopy 2')
            whichsection.push
            (
                <p className={estyle.cssfix}>{bodycopy2}</p>
            )
        }
        console.log(whichsection)
    }


    // useEffect(()=>{
    //     console.log('menupos has changed to: ' + menupos)
    //     renderSection()
    // }, [menupos])


    renderSection()

    return (
        <motion.div className={estyle.container}  ref={scope} 
        variants={svgVariants}
        initial="ready"
        animate="start"
        exit="exit"
        style={{opacity:1}}>

        <motion.div 
            variants={backgroundanim}
            initial="ready"
            animate="start"
            onAnimationComplete={definition => {nextanim(.5)}}
            // onAnimationComplete={definition => {console.log('Completed animating', definition), nextanim(.5)}}
        >  
            <div className={estyle.bgcontainer} id="bg" style={{opacity:0}}/>
            <div className={estyle.contentcontainer}>
                <div className={estyle.col}>
                    <div className={estyle.therow}>
                        <h1 className={estyle.pretitle}>{pretitle}</h1>
                    </div>
                    <div className={estyle.therow}>
                        <h1 className={estyle.title}>{title}</h1>
                    </div>
                    <div className={`${estyle.therow} ${estyle.menugroup}`}>                   
                        <div className={estyle.subcol}>
                            <p className={estyle.menuitem} onClick={(e) => handleClick(e, menu[0], 0)}>{menu[0]}</p>
                        </div>
                        <div className={estyle.subcol}>
                            <p className={estyle.menuitem} onClick={(e) => handleClick(e, menu[1], 1)}>{menu[1]}</p>
                        </div>
                        <div className={estyle.subcol}>
                            <p className={estyle.menuitem} onClick={(e) => handleClick(e, menu[2], 2)}>{menu[2]}</p>
                        </div>
                    </div>
                    <div className={estyle.therow}>
                        {whichsection}
                    </div>
                    {/* <div className={estyle.backgroundshapescontainer}>
                        <div className={estyle.backgroundshapecircle} />
                    </div> */}
                </div>
                <div className={estyle.backgroundshapescontainer}>
                        <div className={estyle.backgroundshapecircle} />
                    </div>

            </div>
        </motion.div> 
    </motion.div>
    )
}
export default About;
