import { motion, useAnimate } from "framer-motion"
import estyle from "./SubModuleContainer.module.scss"
import { useContext, useEffect, useState } from "react";
import SubModulePress from "../sub-module-press/SubModulePress"
import SubModuleTestimonials from "../sub-module-testimonials/SubModuleTestimonials"
import NavContext from "../../../NavContext"

function SubModuleContainer({props}) {


    // left to do here

    // change them to use usecontext - button click on submodule sets state, use context is sent here and this will listen to it's change, once changed then it'll do the roll up, usecontext will be updated, the submodulepress and testimional will retrieve this and 'load' based on which one is set
    // the x button here will also send update usecontext once clicked, and submodules will respond to it accordingly

    // change bg color



    console.log('sub module container')
    console.log(props.delay)
    console.log(props.duration)
    // const [openMenu, setOpenMenu] = useState(false)
    // const [closeMenu, setCloseMenu] = useState(false)
    // const [showSection, setShowSection] = useState(false)
    // const [showPress, setShowPress] = useState(false)


    const { submodules } = useContext(NavContext)
    const { changesubmodule } = useContext(NavContext)
    const [scope, animate] = useAnimate();

    let thesubmoduleis = ''
    let showSection = false;
    let showPress = false;

    //to use the animate it seems like you have to have the style properties you want to animate set on the element and not in a css file
    //you also appear to need your animations to be asyncronous
    //make sure you don't have comma's at the end of your animation lines
    //tenery at bottom 'showPress' seems to be functioning the opposite, shows the component after the colon if true.

    const showPressFunction = (section) => {

        console.log('submodulecontainer - showpressfunction - section is ------: ' + section)

        if(section === 'press')
        {
            console.log('submodulecontainer - showpressfunction - section should be press: ' + section)
            showPress = true

            console.log('showpress is : ' + showPress)
            // showPress = boolean
        }
        else
        {
            console.log('submodulecontainer - showpressfunction - section should be testimonials: ' + section)
            showPress = false

            console.log('showpress is : ' + showPress)
            // showPress = boolean
        }
    }

    const openpanel = async () => {
        await animate("#containerid", { height:"547px", y:-410 }, {duration:.5 })
        await animate("#pressid", { opacity:1 })
        await animate("#closeid", { opacity:1 })
    }

    const closepanel = async () => {
        await animate("#pressid", { opacity:0 })
        await animate("#closeid", { opacity:0 })
        await animate("#containerid", { height:"137px", y:0 }, {duration:.5 })
    }


    //should setshowmodulepress and the other one here because then the sections will know what to show

    submodules.map((item) => {
        console.log('item.submodule is: ' + item.submodule)
        thesubmoduleis = item.submodule
        console.log('thesubmoduleis is: ' + thesubmoduleis)

        if(item.yesopen === true)
        {
            console.log('submodules - map - panel is opening')
            console.log('item.yesopen is: ' + item.yesopen)
            showSection = true

            setTimeout(() => {       
                console.log('settimeout - item.yesopen is: ' + item.yesopen) 
                openpanel()

                console.log('--- item.submodule is: ' + item.submodule)
                showPressFunction(item.submodule)
                // showPressFunction("press")

                // changesubmodule(item.submodule, true)
            }, 500)

        }
        // else if(item.yesopen === false)
        // {
        //     console.log('submodules - map - panel is closing')
        //     console.log('item.yesopen is: ' + item.yesopen)
        //     showSection = true
        //     showPressFunction('testimonials')
        //     closepanel()
        //     setTimeout(() => {        
        //         console.log('settimeout - item.yesopen is: ' + item.yesopen) 
        //         // changesubmodule(item.submodule, false)
        //     }, 500)
        // }
    })

    const handleCloseClick = (e) => {
        console.log('param is: ' + e)
        console.log('The link was clicked.');
        console.log('handleCloseClick thesubmoduleis is: ' + thesubmoduleis)
        // setOpenMenu(false)
        // setCloseMenu(true)

        closepanel()
        setTimeout(() => {        
            changesubmodule('press', false)
            showSection = false
        }, 1500)

        //console.log(e.target.childNodes[0].data.toLowerCase())
        //changesubmodule(e.target.childNodes[0].data.toLowerCase(), false)


    //     const { currentnav } = useContext(NavContext)
    // currentnav.map((navitem) => {
    //     if(navitem.navname === "about"){
    //         console.log('about - navitem navname is:' + navitem.navname)
    //         console.log('about - navitem waittime is:' + navitem.waittime)
    //     }
    //     thedelay = navitem.waittime
    //     console.log('about - thedelay is : ' + thedelay)
    // })

        // changenav(e.target.childNodes[0].data.toLowerCase(), waittime)

    }

    // function updateThatContext()
    // {
    //     console.log('updateThatcontext has been called ----------------- *')
    // }

    // useEffect(() => {

    //     console.log('use effect - sub module container')
    //     console.log('openMenu is: ' + openMenu)
    //     if(openMenu === true){
    //         console.log('use effect - sub module container - openMenu is true')
    //         openpanel();
    //         setCloseMenu(true)
    //     }
    //     else if(closeMenu === true)
    //     {
    //         console.log('use effect - sub module container - openMenu is false')
    //         closepanel();
    //         setOpenMenu(true)
    //     }

    //     console.log('submodules.submodules is: ' + submodules.submodules)
    //     // if(item.isopen === false)
    // //     {
    // //         setOpenMenu(true)
    // //     }

    //     // if(showSubModulePress === true){
    //     //     console.log('press should be shown ----------!!!!!!!!<<<<<<')
    //     // }
    // }, [openMenu])

    const svgVariants = {
        ready: { opacity: 0 },
        start: { opacity: 1, transition: { delay: props.delay, duration : props.duration }}
    }


    /*

    renderContent() {
        if (this.state.loadingPage) {
            return <span className="sr-only">Loading... Registered Devices</span>;
        }

        return (
            <>
            {this.state.someBoolean && <div>some title</div>}
            <div>body</div>
            </>
        );
        }

    */



    const whichsection = []

    const renderSubModules = () => {
        if(showPress = true){
            console.log('submodulecontainer - renderSubModules showPress is: ' + showPress)
        }
    }

    const renderSection = () => {
        if(showSection = true){
            console.log('submodulecontainer - renderSection showsection is ' + showSection)

            // whichsection.push[
            //     <motion.div id="pressid" style={{opacity:0}}>
            //         <div className={estyle.closebutton} id="closeid" style={{ opacity: 0 }} onClick={(e) => handleCloseClick(e)}>x</div>
            //         {}
            //     </motion.div>
            // ]
        }
    }

    renderSubModules()
    renderSection()

    return (

        <div ref={scope}>

            <motion.div 
                id="containerid"
                className={estyle.container} 
                style={{ height: "137px", y:0 }}
                variants={svgVariants}
                initial="ready"
                animate="start"
            >

                {showSection ?
                <motion.div 
                    id="pressid"
                    style={{opacity:0}}
                >
                    <div 
                        className={estyle.closebutton} 
                        id="closeid" 
                        style={{ opacity: 0 }} 
                        onClick={(e) => handleCloseClick(e)}
                    >
                        x
                    </div>
                    {showPress ?
                        <SubModuleTestimonials props={{delay:0, duration:1}}/>
                        :
                        <SubModulePress props={{delay:0, duration:1}}/> 
                    } 
                </motion.div>
                :
                <div>nothing here</div>

                }
            </motion.div>
        </div>
    )
}
export default SubModuleContainer;
