import { createContext, useEffect, useState } from "react";

const NavContext = createContext();

export function NavProvider({children}){

    // const [currentnav, setNav] = useState({ navname: 'default', waittime: 0 })

    const [currentnav, setNav] = useState([])
    // let thenavname = ''
    // let thewaittime = 0


    const changenav = (thename, thetime) => {

        let navname = thename;
        let waittime = thetime
        console.log('setstate - currentnav.navname is: ' + currentnav.navname)
        // console.log('setstate - currentnav.waittime is: ' + currentnav.waittime)
        //console.log(this.state.updated);
        // console.log('changenav params: ' + navname)
        // console.log('changenav params: ' + waittime)

        setNav(() => [{navname, waittime}])

        //console.log('blah - ' + this.state.updated);


        //console.log('currentnav.navname is: ' + currentnav.navname)
        //console.log('currentnav is: ' + currentnav[0])
        // console.log('currentnav.waittime is: ' + currentnav.waittime)

        // console.log('---- check navname and waittime parameters -----')
        // console.log('typeof navname parameter is: ' + typeof(navname))
        // console.log('typeof waittime parameter is: ' + typeof(waittime))
        // console.log('navnmae is: ' + navname)
        // console.log('waittime is: ' + waittime)

        // console.log('\n')

        // console.log('---- check thenavname and thewaittime parameters -----')
        // console.log('typeof thenavname string is: ' + typeof(thenavname))
        // console.log('typeof thewaittime string is: ' + typeof(thewaittime))
        // console.log('thenavname is: ' + thenavname)
        // console.log('thewaittime is: ' + thewaittime)

        // console.log('\n')

        // console.log('---- check currentnav.navname and currentnav.waittime parameters -----')
        // console.log('typeof currentnav.navname is: ' + typeof(currentnav.navname))
        // console.log('typeof currentnav.waittime is: ' + typeof(currentnav.waittime))
        // console.log('currentnav.navname is: ' + currentnav.navname)
        // console.log('currentnav.waittime is: ' + currentnav.waittime)

        // console.log('\n')

        // console.log('---- thenavname and thewaittime are being set to the parameters recieved now ---- ')
        // thenavname = navname;
        // thewaittime = waittime;

        // console.log('\n')

        // console.log('---- check what thenavname and thewaittime are now -----')
        // console.log('typeof thenavname is: ' + typeof(thenavname))
        // console.log('typeof thewaittime is: ' + typeof(thewaittime))
        // console.log('thenavname is: ' + thenavname)
        // console.log('thewaittime is: ' + thewaittime)

        // console.log('\n')

        // console.log('---- setnav is being set to be thenavname and thewaittime now ---- ')
        // setNav(currentnav.navname = thenavname, currentnav.waittime = thewaittime)

        // console.log('\n')

        // console.log('---- check what currentnav.navname and currentnav.waittime are now -----')
        // console.log('typeof currentnav.navname parameter is: ' + typeof(currentnav.navname))
        // console.log('typeof currentnav.waittime parameter is: ' + typeof(currentnav.waittime))
        // console.log('currentnav.navname is: ' + currentnav.navname)
        // console.log('currentnav.waittime is: ' + currentnav.waittime)

        // console.log('\n')
    }


    let firsttime = 0;

    //need to check currentnav has data

    useEffect(() => { 
        console.log('---- use effect ----')

        if(firsttime > 0){
            console.log('firsttime is more than 0')
            console.log('currentnav is: ' + JSON.stringify(currentnav[0]))
            //console.log('currentnav is: ' + JSON.stringify(currentnav[0].navname))
        }

        firsttime++;

    }, [currentnav] 
    ) 

    return(
        <NavContext.Provider value={{currentnav, changenav}}>
            {children}
        </NavContext.Provider>
    )
}
export default NavContext;


