import { motion, useAnimate } from "framer-motion"
import estyle from "./SubModuleDefault.module.scss"
import { useContext, useEffect, useState } from "react";
import NavContext from "../../../NavContext"

function SubModuleDefault({props}) {

    const themodule = props.themodule;
    const { currentnav, submodules, changesubmodule } = useContext(NavContext)
    const [enablebutton, setEnableButton] = useState(false)
    const [isFirstTime, setIsFirstTime] = useState(true)
    const [scope, animate] = useAnimate();
    const whatisit = []

    let amountoftime = .7 * 1000

    const svgVariants = {
        ready: { opacity: 0 },
        start: { opacity: 1, transition: { delay: props.delay, duration : props.duration }}
    }

    const openState = () => {
        setEnableButton(false)
        animate([
            [scope.current, { y:0 }]
            [scope.current, { opacity:1 }]
        ]);
    }

    const collapsedState = () => {
        animate([
            [scope.current, { y:50 }]
        ])
        setTimeout(() => {        
            visibleState()
        }, amountoftime)
        setTimeout(() => {        
            showExpandButton()
        }, amountoftime)
    }

    const invisibleState = () => {
        setEnableButton(false)
        animate([
            [scope.current, { opacity:0 }]
        ])
    }

    const visibleState = () => {
        setEnableButton(false)
        animate([
            [scope.current, { opacity:1 }]
        ])
    }

    const showExpandButton = () => {
        console.log('show the expand button')
        setEnableButton(true)
    }



    useEffect(()=>{

        // NNED TO FIGURE THREE SCENARIOS

                /*
                    no matter what state the site is in, the final state when a nav button is press
                    will be the same, the submodules will be in their collapsed state, the main content
                    area will be in it's expanded state and the submodulecontainer will not be shown

                    switch(item.positionstate) {
                        case 1:
                            do animation so that this component 'collapses'
                            break;
                        case 2:
                            nothing has to change here, or you can just repeat result from case 1
                            break;
                        case 3:
                            wait for the submodulecontainer to animate down, then do the animation from case 1
                            break;
                            default:
                    } 

                    positionstate is at 1 (goes to default state)
                    positionstate is at 2 (goes to main content state)
                    positionstate is at 3 (goes to sub module state)
                    positionstate is at 4 (goes to close button state)

                    1. DEFAULT STATE
                    home default:
                    MC is default home state,
                    SM is default state, 
                    SMC is invisible default state

                    2. MAIN CONTENT STATE
                    menu clicked:
                    MC is open state,
                    SM is collapsed state,
                    SMC is invisible state

                    3. SUB MODULE STATE
                    sub module clicked:
                    MC is same state, no effect
                    SM is invisible state
                    SMC is open state

                    4. CLOSE BUTTON STATE
                    close button clicked:
                    MC is same state, no effect
                    SM is open or collapsed state, visible
                    SMC is invisible default state
                    changesubmodule has a new value - positonstate this will allow these componets to know if the submodule is all the way open, at default state or collapsed.
                */


        // NEED TO MODIFY THIS BLOCK BELOW SO THAT IT 
        // LISTENS FOR POSITIONSTATE INSTEAD OF YESOPEN
        // 
        submodules.map((item) => {

            console.log('33333333333333333333')
            console.log('submoduledefault - item.positionstate is: ' + item.positionstate)
            // if(item.positionstate !== null){
            //     console.log('item.positionstate is not null')
            //     console.log('do animation that collapses this component')
            //     console.log('33333333333333333333')
            // }
            if(isFirstTime === true)
            {
                setIsFirstTime(false)
            }
            else
            {
                console.log('not first time do switch statement')

                switch(item.positionstate) {

                    case 1:
                        ////do animation so that this component 'collapses'
                        //fadeSubModule()
                        openState()
                        break;
                    case 2:
                        ////nothing has to change here, or you can just repeat result from case 1
                        //collapseSubModule()
                        collapsedState()
                        break;
                    case 3:
                        ////wait for the submodulecontainer to animate down, then do the animation from case 1
                        // setTimeout(() => {        
                        //     showSubModule()
                        // }, amountoftime)
                        // setTimeout(() => {        
                        //     collapseSubModule()
                        // }, (amountoftime + 1000))
                        invisibleState()
                        break;
                    case 4:
                        // visibleState()
                        setTimeout(() => {        
                            visibleState()
                        }, amountoftime)
                        break;
                        default:
                } 
            }

            // console.log('submoduledefault - useeffect - item.yesopen is: ' + item.yesopen)
            // if(item.yesopen === true)
            // {        
            //     fadeSubModule()
            // }
            // else if(item.yesopen === false)
            // {
            //     console.log('set to false 6-66-06-00-')
            //     if(isFirstTime === true)
            //     {
            //         console.log('firsttime is true 5095900950509059')
            //         setIsFirstTime(false)
            //     }
            //     else
            //     {
            //         setTimeout(() => {        
            //             showSubModule()
            //         }, amountoftime)
            //     }
            // }
        })
    }, [changesubmodule])

    // useEffect(() => {
    //     currentnav.map((item) => {
    //         console.log('item.navname is: ' + item.navname)

    //         if(item.navname === 'about' || item.navname === undefined)
    //         {

    //         }
    //         else{
    //             fadeSubModule()
    //         }
    //     })
    // }, [currentnav])

    const handleClick = (e) => {
        console.log('The link was clicked.');
        console.log('handleClick')
        submodules.map((item) => {
            console.log('33333333333333333333')
            console.log('submoduledefault - handleClick - item.positionstate is: ' + item.positionstate)
            if(item.positionstate !== null){
                console.log('submoduledefault - handleClick - item.positionstate is not null')
                console.log('do animation that collapses this component')
                console.log('33333333333333333333')
                changesubmodule(e, true, 3)
            }
        })
        //changesubmodule(e, true)
    }

    if(themodule === '1')
    {
        whatisit.push
        (
            <div key={1} className={estyle.contentarea}>
                <div className={estyle.module1col1}>
                    <div className={estyle.contenttitle}>
                        {props.subtitle}
                    </div>
                    <div className={estyle.contentbody}>
                        {props.subbody}
                    </div>
                    <motion.div className={estyle.contentlink} whileHover={{ color:"#03FFFA" }} onClick={() => {handleClick('testimonials')}}>
                        {props.button}
                    </motion.div>
                </div>   
            </div>
        )    
    }
    else
    {
        whatisit.push
        (
            <div key={2} className={estyle.contentarea}>
                <div className={estyle.module2col1}>
                    <div className={estyle.contentimage}>
                    </div>
                </div>
                <div className={estyle.module2col2}>
                    <div className={estyle.contenttitle}>
                        {props.subtitle}
                    </div>
                    <div className={estyle.contentbody}>
                        {props.subbody}
                    </div>
                    <motion.div className={estyle.contentlink} whileHover={{ color:"#03FFFA" }} onClick={() => {handleClick('press')}}>
                        {props.button}
                    </motion.div>
                </div>   
            </div> 
        )
    }

    return (
        <motion.div className={estyle.container}
            ref={scope}
            style={{ y:0, opacity:0 }}
            variants={svgVariants}
            initial="ready"
            animate="start"
        >
            {enablebutton ?
            <div>
                collapse button goes here
            </div> :
            <div/>
            }
            <div className={estyle.title}>
                {props.title}
            </div>
            <div className={estyle.subline} />
            {whatisit}
        </motion.div>
    )
}
export default SubModuleDefault;
