import { motion } from "framer-motion"
import estyle from "./MainNav.module.scss"
import { Link } from "react-router-dom"
import { useContext } from "react"
import NavContext from '../../NavContext'

function MainNav({props}) {

    //need to disable the menu until a section is completely 'loaded in'

    let disablebutton = true;

    const { changenav } = useContext(NavContext)

    const navlabels = ["ABOUT", "PROJECTS", "CLIENTS", "PARTNERS", "FUTURE", "CONTACT"]
    const thenav = []

    // const svgVariants = {
    //     ready: { scaleX:.01, scaleY:0, y:50 },
    //     start: { scaleX:1, scaleY:1, y:0, 
    //         transition: { 
    //             scaleY: { delay: 2.35, duration: .25, type:'tween', ease:'easeIn' },
    //             y: { delay:2.35, duration: .25, type: 'tween', ease:'easeIn' },
    //             scaleX: { delay: 2.6, duration: .25, type:'tween', ease:'backOut' }

    //         }
    //     }
    // }

    const handleClick = (e, waittime) => {
        changenav(e.target.childNodes[0].data.toLowerCase(), waittime)
    }

    const buttoncontrol = () => {
        console.log('control the button')
    }

    for(let i = 0; i < navlabels.length; i++) {
        thenav.push(
            <motion.li key={i} className={estyle.mainnavbtn}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay:props.delay + (i*.2), duration: props.duration }}
            >
                    {/* <Link className={estyle.link} to={`/${navlabels[i]}`.toLowerCase()} onClick={(e) => handleClick(e, props.sectiondelay)}>
                        <motion.div
                            whileHover={{ color:props.hovercolor }}
                            transition={{ duration:props.hoveranimduration}}
                        >
                            {navlabels[i]}
                        </motion.div>
                    </Link> */}
                    {disablebutton ?
                        <Link className={estyle.link} to={`/${navlabels[i]}`.toLowerCase()} onClick={(e) => handleClick(e, props.sectiondelay)}>
                        <motion.div
                            whileHover={{ color:props.hovercolor }}
                            transition={{ duration:props.hoveranimduration}}
                        >
                            {navlabels[i]}
                        </motion.div>
                    </Link> :
                        <div>no link</div>
                    }
            </motion.li>
        )
    }

    return (
        <nav>
            <ul className={estyle.mainnav}>
                {thenav}
            </ul>
        </nav>
    )
}
export default MainNav;
