import { motion, useAnimate } from "framer-motion"
import estyle from "./SubModuleDefault.module.scss"
// import { useContext } from "react";

function SubModuleDefault({props}) {

    const themodule = props.themodule;

    // const { changesubmodule } = useContext(NavContext)
    const [scope, animate] = useAnimate();

    function sequence() {
        animate([
            [scope.current, { y:-400 }],
            // [scope.current, { scale: 1.5 }],
            // [scope.current, { rotate: 0 }],
            // [scope.current, { scale: 1 }]
        ]);
    }

    const handleClick = (e) => {
        //param.preventDefault();
        // console.log('param is: ' + e)
        console.log('The link was clicked.');
        console.log('submoduledefault - handleClick - e.target.childNodes[0].data.toLowerCase() is: ' + e.target.childNodes[0].data.toLowerCase())
        //changesubmodule(e.target.childNodes[0].data.toLowerCase(), false)
        sequence()
    }

    const whatisit = []

    if(themodule === '1')
    {
        // return  <div key={i} className={estyle.greyrectangledotspace}>
        //             <GreyRectangleDot props={{delay:[amountofgreyrectangledotdelay+(i*.05)], width:5, height:5}}/>
        //         </div>

        whatisit.push
        (
            <div key={1} className={estyle.contentarea}>
                <div className={estyle.module1col1}>
                    <div className={estyle.contenttitle}>
                        {props.subtitle}
                    </div>
                    <div className={estyle.contentbody}>
                        {props.subbody}
                    </div>
                    <div className={estyle.contentlink} onClick={() => {handleClick('testimonials')}}>
                        {props.button}
                    </div>
                </div>   
            </div>
        )    
    }
    else
    {
        whatisit.push
        (
            <div key={2} className={estyle.contentarea}>
                <div className={estyle.module2col1}>
                    <div className={estyle.contentimage}>
                    </div>
                </div>
                <div className={estyle.module2col2}>
                    <div className={estyle.contenttitle}>
                        {props.subtitle}
                    </div>
                    <div className={estyle.contentbody}>
                        {props.subbody}
                    </div>
                    <div className={estyle.contentlink} onClick={() => {handleClick('press')}}>
                        {props.button}
                    </div>
                </div>   
            </div> 
        )
    }


    const svgVariants = {
        ready: { opacity: 0 },
        start: { opacity: 1, transition: { delay: props.delay, duration : props.duration }}
    }

    return (
        <motion.div className={estyle.container}
            ref={scope}
            variants={svgVariants}
            initial="ready"
            animate="start"
        >
            <div className={estyle.title}>
                {props.title}
            </div>
            <div className={estyle.subline} />
            {whatisit}
        </motion.div>
    )
}
export default SubModuleDefault;
