import { motion, useAnimationControls } from "framer-motion"
import estyle from "./RectangleDot.module.scss"
import { useEffect } from "react"

function GreyRectangle({props}) {

    useEffect(() => {

        const rectanglesequence = async () => {
            await rectangleControls.start('fadein')
        }
        const dotsequence = async () => {
            await dotControls.start('fadein')
            // await divControls.start('flash1')
            // await divControls.start('flash2')
        }

        rectanglesequence()
        dotsequence()
    })

    const rectangleControls = useAnimationControls();
    const dotControls = useAnimationControls();


    const rectangledotanim = {
        ready: { opacity: 0, width:0, backgroundColor: "#CCCCCB"},
        fadein: { opacity:1, width:props.width, backgroundColor: "#CCCCCB",
            transition: {
                opacity: { delay:props.delay, duration: .2 },
                // scale: { delay: props.delay, duration: .4, type:'tween', ease:'backInOut' }
            }
        },

        // flash1: { opacity: 0,
        //     transition: {
        //         outline: {delay: 0, duration: .05}
        //     }
        // },
        // flash2: { opacity: 1,
        //     transition: {
        //         outline: {delay: 0, duration: .05}
        //     }
        // },
    }

    const dotanim = {
        ready: { opacity: 0, width:0, backgroundColor: "#CCCCCB"},
        fadein: { opacity:1, width:props.width, backgroundColor: "#CCCCCB",
            transition: {
                opacity: { delay:props.delay, duration: .2 },
                // scale: { delay: props.delay, duration: .4, type:'tween', ease:'backInOut' }
            }
        },

        // flash1: { opacity: 0,
        //     transition: {
        //         outline: {delay: 0, duration: .05}
        //     }
        // },
        // flash2: { opacity: 1,
        //     transition: {
        //         outline: {delay: 0, duration: .05}
        //     }
        // },
    }


    return (
        <motion.div className={estyle.rectangledot}
            variants={rectangledotanim}
            initial="ready"
            animate={rectangleControls}
        >

        </motion.div>

        // <motion.div className={estyle.dot}
        //     variants={dotanim}
        //     initial="ready"
        //     animate={dotControls}
        // >

        // </motion.div>
    )
}
export default GreyRectangle;
