import { motion } from "framer-motion"
// import estyle from "./Radar.module.scss"

function Radar({props}) {

    const startdot = {
        ready: { opacity:0, rotate:props.rotate },
        start: { opacity:1, rotate:props.rotateto, 
            transition: {
                opacity: {
                    delay:props.delay,
                    duration:.3
                },
                rotate: { 
                    delay: props.delay, 
                    duration: props.rotateduration, 
                    type:'tween', 
                    ease:'easeOut' 
                },
            }}
    }

    const svgVariants = {
        hidden: { opacity: 0, rotate:props.rotate },
        visible: { opacity: 1, rotate:props.rotateto, transition: { delay: props.delay, duration : 1 }}
    }

    return (
        <div>
            <motion.svg width="25" height="25" viewBox="0 0 25 25" fill="none" xmlns="http://www.w3.org/2000/svg"
                variants={svgVariants}
                initial="hidden"
                animate="visible"
            >
                <path d="M12.3342 23.5558C18.6578 23.5558 23.7842 18.4294 23.7842 12.1058C23.7842 5.7821 18.6578 0.655762 12.3342 0.655762C6.01051 0.655762 0.884155 5.7821 0.884155 12.1058C0.884155 18.4294 6.01051 23.5558 12.3342 23.5558Z" stroke={props.color} strokeMiterlimit="1.5" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M20.2447 3.50665L11.7594 11.992L12.4665 12.6991L20.9518 4.21378L20.2447 3.50665Z" fill={props.color}/>
            </motion.svg>
        </div>
    )
}
export default Radar;
