import { motion, useAnimationControls } from "framer-motion"
import estyle from "./ASquare.module.scss"
import { useEffect } from "react"

function ASquare({props}) {

    useEffect(() => {

        const sequence = async () => {
            await divControls.start('fadein')
            await divControls.start('flash1')
            await divControls.start('flash2')
            await divControls.start('flash3')
            await divControls.start('flash4')
            await divControls.start('flash5')
            // await divControls.start('start')
            // divControls.start('start2')
        }

        sequence()
    })

    const divControls = useAnimationControls();

    const hrlinenim = {
        ready: { opacity: 0, scale:.5, outline: '1px solid #ffffff'},
        fadein: { opacity:1, scale:1, outline: '1px solid black',
            transition: {
                outline: '1px solid #ffffff',
                opacity: { delay:props.delay, duration: .2 },
                // x: { delay: props.delay, duration: .2, type:'tween', ease:'easeInOut' },
                scale: { delay: props.delay, duration: .4, type:'tween', ease:'backInOut' }
            }
        },

        flash1: { outline: '1px solid #ffffff',
            transition: {
                outline: {delay: 0, duration: .05}
            }
        },
        flash2: { outline: '1px solid #000000',
            transition: {
                outline: {delay: 0, duration: .05}
            }
        },
        flash3: { outline: '1px solid #ffffff',
            transition: {
                outline: {delay: 0, duration: .05}
            }
        },
        flash4: { outline: '1px solid #000000',
            transition: {
                outline: {delay: 0, duration: .05}
            }
        },
        flash5: { outline: '1px solid #ffffff',
            transition: {
                outline: {delay: 0, duration: .05}
            }
        },

        // start: { x: 20, width:110,
        //     transition: { 
        //         x: { delay: 0, duration: .2, type:'tween', ease:'easeInOut' },
        //         width: { delay: 0, duration: .2, type:'tween', ease:'easeInOut' }
        //     }    
        // },
        // start2: { x: 0, width:50, borderTop:'1px solid #000000',
        //     transition: { 
        //         x: { delay: 0, duration: .125, type:'tween', ease:'easeInOut' },
        //         width: { delay: 0, duration: .125, type:'tween', ease:'easeInOut' },
        //         borderTop: {delay: 0, duration: .125}
        //     }    
        // }
    }


    return (
        <div>
            <motion.div className={estyle.thesquare}
                variants={hrlinenim}
                initial="ready"
                animate={divControls}
             />

            {/* <motion.svg className={estyle.thesquare} width="12" height="12" viewBox="0 0 12 12" fill="none" xmlns="http://www.w3.org/2000/svg"
                variants={svgVariants}
                initial="hidden"
                animate="visible"
            >
                <path d="M20.9852 0.721436H0.985596V20.7211H20.9852V0.721436Z" fill={props.color}/>

            </motion.svg> */}

        </div>
    )
}
export default ASquare;
