import { motion, useAnimate } from "framer-motion"
import estyle from "./Partners.module.scss"
import NavContext from "../../NavContext"
import { useContext, useState } from "react"

function Partners({props}) {

    const [bgimagename, setBgImageName] = useState('/images/sections/partners-template.jpg');

    let pretitle = 'OUR'
    let title = 'PARTNERS'
    let bodycopy0 = "WITHOUT OUR PARTNERS \nWE WOULDN'T BE ABLE TO \nCREATE THE AMAZING \nTECHNOLOGY THAT WE MAKE. \nOUR POINT OF VIEW IS THAT \nOUR PARTNERS ARE ON THIS  \nJOURNEY WITH US, THROUGH \nIT ALL OUR PARTNERS  \nARE THERE TO GUIDE AND HELP \nUS ACHIEVE THE GOALS WE \nSET TO ACHIEVE. "
    let thelist = ['X', 'IBM', 'META', 'GOOGLE', 'ADOBE ILLUSTRATOR']
    let thedelay = 0;
    let theduration = 0;

    const [scope, animate] = useAnimate();
    const { currentnav } = useContext(NavContext)

    currentnav.map((navitem) => {
        thedelay = navitem.waittime
        theduration = navitem.duration
    })

    const svgVariants = {
        ready: { opacity:0 },
        start: { opacity:1, 
            transition: { 
                opacity: { delay:thedelay, duration:theduration }
            }
        },
        exit: {opacity:0, transition: {delay:0, duration:theduration}}
    }

    const backgroundanim = {
        ready: { opacity:0 },
        start: { opacity:1,
            transition: {
                opacity: {delay:thedelay, duration:theduration}
            }
        }
    }

    const nextanim = async (delay) => { 
        await animate('#bg', { opacity:1, x:0})
    }

    const listofcompanies = []
    for(let i = 0; i < thelist.length; i++) {
        listofcompanies.push(
            <li className={estyle.listitems}>{thelist[i]}</li>
        )
    }

    return (
        <motion.div className={estyle.container}  ref={scope} 
        variants={svgVariants}
        initial="ready"
        animate="start"
        exit="exit"
        style={{opacity:1}}>

        <motion.div 
            variants={backgroundanim}
            initial="ready"
            animate="start"
            onAnimationComplete={definition => {nextanim(.5)}}
        >  
            <div className={`${estyle.bgcontainer}`} id="bg" style={{ backgroundImage:`url(${bgimagename})`, opacity:0 }}/>
            <div className={estyle.contentcontainer}>
                <div className={estyle.thecontent}>
                    <div className={estyle.therow}>
                        <h1 className={estyle.pretitle}>{pretitle}</h1>
                    </div>
                    <div className={estyle.therow}>
                        <h1 className={estyle.title}>{title}</h1>
                    </div>
                    <motion.div className={estyle.therow} id="contentsection" style={{ opacity:1 }}>
                        <p className={estyle.cssfix}>{bodycopy0}</p>
                    </motion.div>
                </div>
                <div className={estyle.thespacer} />
                <motion.ul className={estyle.thelist} id="contentsection" style={{ opacity:1 }}>
                    {listofcompanies}
                </motion.ul>
            </div>
        </motion.div> 
    </motion.div>
    )
}
export default Partners;
