import { motion, useAnimate, useAnimationControls } from "framer-motion"
import estyle from "./ASquare.module.scss"
import { useEffect } from "react"

function ASquare({props}) {

    // useEffect(() => {

    //     const sequence = async () => {
    //         await divControls.start('fadein')
    //         await divControls.start('flash1')
    //         await divControls.start('flash2')
    //     }

    //     sequence()
    // })

    // const divControls = useAnimationControls();

    async function path1animate(){
        await animate("#path1", {opacity:0, scale:.5, outline:'1px solid #ffffff'}, { delay:2, duration:.1})
        await animate("#path1", {opacity:1, scale:1, outline:'1px solid black'}, { transition:{ outline: '1px solid #ffffff', opacity: { delay:props.delay, duration: .2 }, scale: { delay: props.delay, duration: .4, type:'tween', ease:'backInOut' }}})
        await animate("#path1", {opacity:0}, { delay:0, duration:.1})
        await animate("#path1", {opacity:1}, { delay:0, duration:.1})
        // await animate("#path1", {opacity:0}, { delay:0, duration:.1})
        // await animate("#path1", {opacity:1}, { delay:0, duration:.1})
        // await animate("#path1", {opacity:0}, { delay:0, duration:.1})
        // await animate("#path1", {opacity:1}, { delay:0, duration:.1})
        // await animate("#path1", {opacity:0}, { delay:0, duration:.1})
        // await animate("#path1", {opacity:1}, { delay:0, duration:.1})
    }

    const [scope, animate] = useAnimate()
    const [isReady, setIsReady] = useState(false)

    useEffect(() => {
        setTimeout(() => {        
            setIsReady(true)
        }, 2400)
        path1animate();
    }, [])

    const hrlinenim = {
        ready: { opacity: 0, scale:.5, outline: '1px solid #ffffff'},
        fadein: { opacity:1, scale:1, outline: '1px solid black',
            transition: {
                outline: '1px solid #ffffff',
                opacity: { delay:props.delay, duration: .2 },
                scale: { delay: props.delay, duration: .4, type:'tween', ease:'backInOut' }
            }
        },

        flash1: { opacity: 0,
            transition: {
                outline: {delay: 0, duration: .05}
            }
        },
        flash2: { opacity: 1,
            transition: {
                outline: {delay: 0, duration: .05}
            }
        },
    }


    return (
        <div>
            <motion.div 
                ref={scope}
                // className={estyle.thesquare}
                className={`${estyle.thesquare} ${(isReady === true ? estyle.isnotvisible : estyle.isvisible)}`}
                // className={(isReady === true ? estyle.isnotvisible : estyle.isvisible)}
                id="path1"
                // variants={hrlinenim}
                // initial="ready"
                // animate={divControls}
             />
        </div>
    )
}
export default ASquare;
