import { motion } from "framer-motion"
import estyle from "./MainNav.module.scss"
import { useNavigate } from "react-router-dom"
import { useState, useContext, useEffect } from "react"
import NavContext from '../../NavContext'

function MainNav({props}) {

    console.log('MAINNAV SECTION-')

    const menudisabledamount = props.disablemenuamount * 1000
    const [enablebutton, setEnableButton] = useState(true)
    const [isPrevActive, setPrevActive] = useState('')
    const [isActive, setActive] = useState('home');
    const [isFirstTime, setIsFirstTime] = useState(true)
    const [firstTimeClick, setFirstTimeClick] = useState(true)
    const { currentnav, changenav, submodules, changesubmodule, changeHomePage, homepagevisible } = useContext(NavContext)
    const navlabels = ["ABOUT", "PROJECTS", "CLIENTS", "PARTNERS", "FUTURE", "CONTACT"]
    const thenav = []
    const navigate = useNavigate()

    let delaybeforenavchange = .1

    const handleHoverVariant = {
        fromselected: {color: '#03FFFA'}, /* teal */
        tounselected: { color:'#292728',  /* charcoal */ 
            transition: { 
                color:{ duration: props.hoveranimduration }
            }
        },
        hoveranim: {
            color:props.hovercolor,
            transition:{ duration:props.hoveranimduration }
        }
    }

    const handleClick = (e, waittime, duration, label) => {

        if(isActive !== label)
        {
            setEnableButton(false)

            setTimeout(() => {        
                setEnableButton(true)
            }, menudisabledamount)

            setPrevActive(isActive)

            setActive(label);


            // might need to bring this back in 

            // if(firstTimeClick === true){
                //     if(submodules.length === 0)
                //     {
                    //         console.log('submodules is blank')
                    //         setFirstTimeClick(false)
                    //         navigatetourl(`/${e.target.childNodes[0].data.toLowerCase()}`, e.target.childNodes[0].data.toLowerCase(), waittime, duration, delaybeforenavchange)
                    //     }
                    // }

            submodules.map((item) => {

                console.log('mainnav')

                if(item.positionstate !== null){
                    if(homepagevisible === true)
                    {
                        //THIS IS CAUSING ALL THE RE-RENDERS IN SUBMODULEDEFAULT

                        changeHomePage(false, false)
                        setTimeout(() => {        
                            changesubmodule('', false, 2)
                            navigatetourl(`/${e.target.childNodes[0].data.toLowerCase()}`, e.target.childNodes[0].data.toLowerCase(), waittime, duration, .5)
                        }, 700)
                    }
                    else
                    {
                        changesubmodule('', false, 2)
                        navigatetourl(`/${e.target.childNodes[0].data.toLowerCase()}`, e.target.childNodes[0].data.toLowerCase(), waittime, duration, .5)
                    }
                }
            })
        }
    }

    const navigatetourl = (url, label, waittime, duration, thedelay) => {
        let delay = thedelay * 1000
        setTimeout(() => {        
            navigate(url);
            changenav(label, waittime, duration)
        }, delay)
    }

    const checker = () => {
        console.log('checker')
        submodules.map((item) => {
            if(item.submodule === 'home'){
                console.log('item.submodule is: ' + item.submodule)
                changenav("ABOUT", props.sectiondelay, props.sectionduration)
            }
        })
    }

    useEffect(() => {
        console.log('mainnav - useeffect - chagnesubmodule(" ", false) SECTION-')
        console.log('------------------------------------------------- SECTION-')
        changesubmodule('', false)
    }, [])

    useEffect(() => {
        checker()
        //changesubmodule('', false, 2)
    }, [submodules])

    // useEffect(() => {

    //     if(isFirstTime === true){
    //         console.log('firsttime is ' + isFirstTime)
    //         console.log('props.sectiondelay is: ' + props.sectiondelay)
    //         // changenav('about', props.sectiondelay)
    //         changesubmodule('', false)
    //         setIsFirstTime(false)
    //     }
    //     else
    //     {
    //         console.log('useeffect - else -  ' + isFirstTime)
    //         console.log('firsttime is ' + isFirstTime)
    //         setTimeout(() => {        
    //             setPrevActive('')
    //         }, 3000)
    //     }
    // }, [isPrevActive])

    // useEffect(() => {

    //     currentnav.map((item) => {
    //         setActive(item.navname)
    //         // setPrevActive(item.navname)
    //     })
    // },[currentnav])



    for(let i = 0; i < navlabels.length; i++) {
        thenav.push(
            <motion.li key={i} className={estyle.mainnavbtn}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay:props.delay + (i*.2), duration: props.duration }}
            >
                {enablebutton ?
                    <div className={`
                            ${estyle.link} ${navlabels[i].toLowerCase()} 
                            ${(isActive === navlabels[i].toLowerCase() ? null : estyle.selected)}`} 
                            onClick={(e) => handleClick(e, props.sectiondelay, props.sectionduration, navlabels[i].toLowerCase())}
                    >
                        {isPrevActive === navlabels[i].toLowerCase() ?
                        <motion.div 
                            className={estyle.link}
                            variants={handleHoverVariant}
                            initial="fromselected"
                            animate="tounselected"
                            whileHover={"hoveranim"}
                        >
                        {navlabels[i]}
                        </motion.div> :
                        <motion.div
                            className={`${estyle.link} ${(isActive === navlabels[i].toLowerCase() ? estyle.selected : null)}`}
                            variants={handleHoverVariant}
                            whileHover={"hoveranim"}

                        >
                            {navlabels[i]}
                        </motion.div> 
                        }
                    </div> :
                    <div className={`${estyle.link} ${(isPrevActive === navlabels[i].toLowerCase() ? estyle.selected : null)} ${(isActive === navlabels[i].toLowerCase() ? estyle.selected : null )}`}>{navlabels[i]}</div>
                }
            </motion.li>
        )
    }

    return (
        <nav>
            <ul className={estyle.mainnav}>
                {thenav}
            </ul>
        </nav>
    )
}
export default MainNav;
