import { motion } from "framer-motion"
import estyle from "./MainNav.module.scss"
import { Link } from "react-router-dom"
import { useState, useContext } from "react"
import NavContext from '../../NavContext'

function MainNav({props}) {
    const menudisabledamount = props.disablemenuamount * 1000
    const [enablebutton, setEnableButton] = useState(true)
    const [isActive, setActive] = useState('about');
    const { changenav } = useContext(NavContext)
    const navlabels = ["ABOUT", "PROJECTS", "CLIENTS", "PARTNERS", "FUTURE", "CONTACT"]
    const thenav = []


    //need to figure out how to animate the unclicked nav item back to it's original colour. right now it just flips back.

    const handleHoverVariant = {
        ready: {color: '#FFD62A'},
        start: { color:'#ECEBEB', 
            transition: { 
                color:{ duration: props.hoveranimduration }
            }
        },
        hover: {
            color:props.hovercolor,
            transition:{ duration:props.hoveranimduration }
        }
    }

    const handleClick = (e, waittime, label) => {

        if(isActive != label)
        {
            setEnableButton(false)

            setTimeout(() => {        
                setEnableButton(true)
            }, menudisabledamount)

            setActive(label);
            changenav(e.target.childNodes[0].data.toLowerCase(), waittime)
        }
    }

    for(let i = 0; i < navlabels.length; i++) {
        thenav.push(
            <motion.li key={i} className={estyle.mainnavbtn}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay:props.delay + (i*.2), duration: props.duration }}
            >
                {enablebutton ?
                    <Link className={`${estyle.link} ${navlabels[i].toLowerCase()} ${(isActive == navlabels[i].toLowerCase() ?  estyle.speciallink : null)}`} to={`/${navlabels[i]}`.toLowerCase()} onClick={(e) => handleClick(e, props.sectiondelay, navlabels[i].toLowerCase())}>
                        {isActive == navlabels[i].toLowerCase() ?
                        <motion.div
                            variants={handleHoverVariant}
                            initial="ready"
                            animate="start"
                        >
                        {navlabels[i]}
                        </motion.div> :
                        <motion.div
                            variants={handleHoverVariant}
                            // initial={isActive === i ? "initial" : "blahblah"}
                            whileHover={isActive === navlabels[i].toLowerCase() ? "initial" : "hover"}

                        >
                            {navlabels[i]}
                        </motion.div> 
                        }
                    </Link> :
                    <div className={estyle.nolink} to={`/${navlabels[i]}`.toLowerCase()}>{navlabels[i]}</div>
                }
            </motion.li>
        )
    }

    return (
        <nav>
            <ul className={estyle.mainnav}>
                {thenav}
            </ul>
        </nav>
    )
}
export default MainNav;
