import { motion, useAnimate } from "framer-motion"
import estyle from "./Home.module.scss"
import NavContext from "../../NavContext"
import { useContext, useEffect, useRef } from "react"

function Home({props}) {


    console.log('HOME SECTION-')

    //the mapping has to happen before the variants animation so that
    //it can set 'thedelay' number before the variant animation uses it

    let thedelay = 0;
    let theduration = .25;

    const firsttimeref = useRef(true)
    const [scope, animate] = useAnimate();
    const { currentnav, changeHomePage, homepagevisible } = useContext(NavContext)

    const fadeOut = () => {
        animate(scope.current, { opacity:0 })
    }
    const fadeIn = async (delayamount) => {

        let settimeoutdelay = delayamount * 1000;

        animate('#bg', {x:-100})
        animate('#title', {x:-100})
        await animate('#bg', { opacity:1, x:0}, {delay:delayamount})
        await animate('#title', { x:0, opacity:1 })

        setTimeout(() => {        
            changeHomePage(true, false)
        }, settimeoutdelay)

    }

    currentnav.map((navitem) => {
        // if(navitem.navname === "home"){
        // }
        thedelay = navitem.waittime
        theduration = navitem.duration

    })

    useEffect(() => {

        homepagevisible.map((item) => {
            console.log('item.homepagevisible is: ' + item.homepagevisible)
            console.log('item.isfirsttime is: ' + item.isfirsttime)

            if(item.homepagevisible === false)
            {
                fadeOut()
            }
            else
            {
                if(item.isfirsttime === true)
                {
                    fadeIn(6)
                }
                else if(item.isfirsttime === false)
                {
                    fadeIn(1)
                }
            }
        })
    }, [homepagevisible])

    return (
        <motion.div className={estyle.container}  ref={scope} style={{opacity:1}}>

            <div className={estyle.bgcontainer} id="bg" style={{opacity:0}}/>
            <div className={estyle.contentcontainer}>
                {/* <div className={estyle.col}>
                    col 1
                </div>
                <div className={estyle.col}>
                    col 2
                </div> */}
                <div className={estyle.col}>
                    <div className={estyle.title} id="title" style={{opacity:0}}><span className={estyle.pinkletter}>A</span>TOM<span className={estyle.pinkletter}>I</span></div>
                </div>
            </div>

        </motion.div>
    )
}
export default Home;
