    const firsttimeref = useRef(true)
    const [menupos, setMenuPos] = useState(0)

    let whichsection = []
    let hovercolor = '#FFD62A'
    let hoveranimduration = 1

    //the mapping has to happen before the variants animation so that
    //it can set 'thedelay' number before the variant animation uses it

    /*

        title:"RECOGNITION",
        subtitle:"Meticulously Designed",
        subbody:"[company] has been meticulously designed and trained to tackle all challenges with an extraordinary blend of wit...",
        button: "view all"


    */

    let pretitle = 'THE'
    let title = 'PROJECTS'
    let menu = ['CREATING CREATION', 'THE SMARTEST AI']
    let bodycopy0 = "At Atomi, we pride ourselves in creating things that previously didn't exist. Our goal is to create \ntechnology that enriches the world and the humans in it, by advancing AI to a point where \nthe customer, eventually will barely have to think about something before one of our creations \ngrant it to them. You can Trust Atomi to improve life as we know it."
    let bodycopy1 = "Atomi has long been known for its innovative and creative \napproach to tackling obstacles and capturing opportunity. In recent years, \nthe organization has decided to focus its attention on \nbuilding the smartest AI to date. To this end, the company has set up a project team specifically dedicated to the task. The project team is tasked with coming up with innovative ways to create and develop an AI that can think and act just like humans. This includes creating efficient algorithms for tasks such as natural language processing, vision recognition, and robotics."

    let thedelay = 0;
    let theduration = 0;

    const [scope, animate] = useAnimate();
    const { currentnav } = useContext(NavContext)


    currentnav.map((navitem) => {
        thedelay = navitem.waittime
        theduration = navitem.duration
    })

    const svgVariants = {
        ready: { opacity:0 },
        start: { opacity:1, 
            transition: { 
                opacity: { delay:thedelay, duration:theduration }
            }
        },
        exit: {opacity:0, transition: {delay:0, duration:theduration}}
    }

    const backgroundanim = {
        ready: { opacity:0 },
        start: { opacity:1,
            transition: {
                opacity: {delay:thedelay, duration:theduration}
            }
        }
    }

    const nextanim = async (delay) => { 
        await animate('#bg', { opacity:1, x:0})
    }

    const handleHover = (label) => {

        if(isButtonDisabled !== label)
        {
            let newlabel = '#' + label
            animate(newlabel, { color:'#FFD62A' })
        }
    }

    const handleLeave = (label) => {

        console.log('handleLeave')

        if(isButtonDisabled !== label)
        {
            let newlabel = '#' + label
            animate(newlabel, { color:'#000000' })
        }
    }

    const removeDisabledColor = (label) => {

        let newlabel = '#' + label
        animate(newlabel, { color:'#000000' })
    }

    const switchBg = async (bg) => {
        setTimeout(() => {        
            setBgImageName(bg)
        }, 500)

        await animate('#bg', {opacity:0, x:5, duration:.5})
        await animate('#bg', {x:5, delay:1})
        await animate('#bg', {opacity:1, x:0, duration:.5, delay:1})
    }

    const handleClick = (e, label, num) => {

        if(num === menupos)
        {
            console.log('do nothing')
        }
        else
        {

            setMenuPos(num)
            console.log('menupos is: ' + num)

            switch(num) {
                case 0:
                    setShowSection0(true)
                    setShowSection1(false)
                    setShowSection2(false)
                    // setBgImageName('/images/sections/about-who-we-are.jpg')
                    switchBg('/images/sections/projects-creating-creation-template.jpg')
                    setButtonDisabled('button1');
                    removeDisabledColor('button2')
                    // animateRenderedSection()
                    renderSection()
                    break;
                case 1:
                    setShowSection0(false)
                    setShowSection1(true)
                    setShowSection2(false)
                    // setBgImageName('/images/sections/about-our-approach.jpg')
                    switchBg('/images/sections/projects-the-smartest-ai-template.jpg')
                    setButtonDisabled('button2');
                    removeDisabledColor('button1')
                    // animateRenderedSection()
                    renderSection()
                    break;
                // case 2:
                //     setShowSection0(false)
                //     setShowSection1(false)
                //     setShowSection2(true)
                //     // setBgImageName('/images/sections/about-our-vision.jpg')
                //     switchBg('/images/sections/about-our-vision.jpg')
                //     setButtonDisabled('button3');
                //     removeDisabledColor('button1')
                //     removeDisabledColor('button2')
                //     // animateRenderedSection()
                //     renderSection()
                //     break;
                default:
            }
        }
    }


