    //         await animate('#contentsection', {opacity:.5, duration:.5})
    //         await animate('#contentsection', {opacity:1, duration:.5})
    //         // setTimeout(() => {        
    //         //     renderSection()
    //         // }, 500)
    //     }
    //     else
    //     {
    //         console.log('firsttimeref is ^^^^^^^^^: ' + firsttimeref.current)
    //         console.log('setting firsttimeref to false !!!!!!!!!!!!!!!!933030595305305439059 ' + firsttimeref.current)
    //         // setfirsttime(firsttimeref)
    //         firsttimeref.current = false
    //         renderSection()
    //     }
    // }

    const renderSection = () => {
        whichsection = []

        if(showSection0 === true) {
            console.log('should be bodycopy 0')
            whichsection.push
            (
                <p className={estyle.cssfix}>{bodycopy0}</p>
            )
        }
        else if(showSection1 === true){

            console.log('should be bodycopy 1')
            whichsection.push
            (
                <p className={estyle.cssfix}>{bodycopy1}</p>
            )
        }
        // else if(showSection2 === true){

        //     console.log('should be bodycopy 2')
        //     whichsection.push
        //     (
        //         <p className={estyle.cssfix}>{bodycopy2}</p>
        //     )
        // }
    }

    // animateRenderedSection()
    renderSection()

    return (
        <motion.div className={estyle.container}  ref={scope} 
        variants={svgVariants}
        initial="ready"
        animate="start"
        exit="exit"
        style={{opacity:1}}>

        <motion.div 
            variants={backgroundanim}
            initial="ready"
            animate="start"
            onAnimationComplete={definition => {nextanim(.5)}}
        >  
            <div className={`${estyle.bgcontainer}`} id="bg" style={{ backgroundImage:`url(${bgimagename})`, opacity:0 }}/>

        </motion.div>

            <div className={estyle.allthecontent}>

                <div className={estyle.row1}>
                    <p className={estyle.thep}>row 1</p>
                </div>

                <div className={estyle.row2}>

                    <div className={estyle.subrow1}>
                    <p className={estyle.thep}>row 2 subrow 1</p>
                    </div>

                    <div className={estyle.subrow2}>
                    <p className={estyle.thep}>row 2 subrow 2</p>
                    </div>


                    {/* <div className={estyle.col1}>
                        <p className={estyle.thep}>row 2 subrow 1</p>
                    </div>
                    <div className={estyle.col2}>
                        <p className={estyle.thep}>row 2 subrow 2</p>
                    </div>
                    <div className={estyle.row3}>
                        <p className={estyle.thep}>row 2 col 2</p>
                    </div> */}
                </div>

            </div>




            {/* <div className={estyle.contentcontainer}>
                <div className={estyle.thecontent}>
                    <div className={estyle.therow}>
                        <h1 className={estyle.pretitle}>{pretitle}</h1>
                    </div>
                    <div className={estyle.therow}>
                        <h1 className={estyle.title}>{title}</h1>
                    </div>
                </div>
                <div className={estyle.themenucontent}>
                    <div className={`${estyle.therow} ${estyle.menugroup}`}>                   
                        <div className={estyle.col}>
                            <motion.div 
                                className={`${estyle.menuitem} ${(isButtonDisabled === 'button1' ? estyle.disabledButton : estyle.menu)}`}
                                id="button1"
                                onMouseEnter={() => handleHover('button1')}
                                onMouseLeave={() => handleLeave('button1')}
                                onClick={(e) => handleClick(e, menu[0], 0)}
                            >
                                {menu[0]}
                            </motion.div>
                        </div>
                        <div className={estyle.col}>
                            <motion.div 
                                className={`${estyle.menuitem} ${(isButtonDisabled === 'button2' ? estyle.disabledButton : estyle.menu)}`}
