    let amountoftime = .7 * 1000
    let delaybeforenavchange = .1
    let linkpos = 0

    const svgVariants = {
        ready: { opacity: 0 },
        start: { opacity: 1, transition: { delay: props.delay, duration : props.duration }}
    }

    const openState = () => {
        setEnableButton(false)
        animate([
            [scope.current, { y:0 }]
        ])
        setTimeout(() => {        
            visibleState()
        }, amountoftime)
    }

    // const collapsedState = () => {
    //     animate([
    //         [scope.current, { y:0 }],
    //         ["#contenttitle", {opacity:.5}],
    //         ["#contentbody", {opacity:.5}]
