         animate("#contentlink", { opacity:0 }, {delay: .2})


        // setTimeout(() => {        
        //     visibleState()
        // }, amountoftime)
        setTimeout(() => {        
            showExpandButton()
            collapsedFinishedState()
        }, amountoftime)
    }

    const collapsedFinishedState = async () => {
        await animate("#contentlink", { y:-65, x:linkpos })
        await animate("#contentlink", { opacity:1 })
    }

    const invisibleState = () => {
        // setEnableButton(false)
        animate([
            [scope.current, { opacity:0 }]
        ])
    }

    // const visibleState = () => {
    //     // setEnableButton(false)
    //     animate([
    //         [scope.current, { opacity:1 }]
    //     ])
    // }

    const visibleState = async () => {
        await animate(scope.current, { opacity:1 })
