        console.log("handleExpandClick 8888888888888888")
        animate(".SubModuleDefault_contentlink1__myr4N", { opacity:0 })
        animate(".SubModuleDefault_contentlink2__myr4N", { opacity:0 })
        changesubmodule("home", false, 1)

        let delay = .25 * 1000
        setTimeout(() => {        
