                        {props.subbody}
                    </motion.div>
                    <motion.div className={`${estyle.contentlink} ${estyle.contentlink2}`} id="contentlink" style={{y:0, x:0}} whileHover={{ color:"#03FFFA" }} onClick={() => {handleClick('press')}}>

                    {/* <motion.div className={estyle.contentlink} id="contentlink" style={{y:0, x:0}} whileHover={{ color:"#03FFFA" }} onClick={() => {handleClick('press')}}> */}
                        {props.button}
                    </motion.div>
                </div>   
            </div> 
        )
    }

    return (
        <motion.div className={estyle.container}
            ref={scope}
            style={{ y:0, opacity:0 }}
            variants={svgVariants}
            initial="ready"
            animate="start"
        >
            {showcollapsebutton}
            <div className={estyle.title}>
                {props.title}
            </div>
            <div className={estyle.subline} />
            {whichsection}
        </motion.div>
    )
}
export default SubModuleDefault;
