import { motion, useAnimate } from "framer-motion"
import estyle from "./MainContentBox.module.scss"
import AnimatedRoutes from "../../navigation/AnimatedRoutes"
import { useState, useContext, useEffect } from "react"
import NavContext from "../../NavContext"

const MainContentBox = ({props}) => {

    const { submodules } = useContext(NavContext)
    const [scope, animate] = useAnimate();

    let amountoftime = .7 * 1000

    const svgVariants = {
        ready: { scaleX:.01, scaleY:0, y:50 },
        start: { scaleX:1, scaleY:1, y:0, 
            transition: { 
                scaleY: { delay: 2.35, duration: .25, type:'tween', ease:'easeIn' },
                y: { delay:2.35, duration: .25, type: 'tween', ease:'easeIn' },
                scaleX: { delay: 2.6, duration: .25, type:'tween', ease:'backOut' }

            }
        }
    }

    const defaultpanel = async () => {
        // animate([
        //     ['#routecontainerid', {opacity:0}, {duration:.2}]
        //     [scope.current, { height:"410px" }, {duration: .25, delay:.2 }]
        // ]);

        animate('#routecontainerid', {opacity:0}, {duration:.2})
        animate(scope.current, { height:"410px" }, {duration: .25, delay:2.2 })
        await animate('#routecontainerid', {opacity:1}, {duration:.2, delay:.45})
    }

    const openpanel = () => {
        animate([
            [scope.current, { height:"550px" }, {duration: .25, delay:.2 }]
        ]);
    }

    /*
    positionstate is at 1 (goes to default state)
    positionstate is at 2 (goes to main content state)
    positionstate is at 3 (goes to sub module state)
    positionstate is at 4 (goes to close button state)
    */

    useEffect(() => {
        submodules.map((item) => {
            switch(item.positionstate) {
                case 1:
                    defaultpanel()
                    break;
                case 2:
                    setTimeout(() => {        
                        openpanel()
                    }, amountoftime)
                    // openpanel()
                    break;
                case 3:
                    // openpanel() there should be no change, so whatever state this component is in just do nothing here
                    break;
                case 4:
                    // openpanel() there should be no change, so whatever state this component is in just do nothing here
                    break;
                    default:
            }
        })
    })

    return (
        <div>
            <motion.div 
                ref={scope}
                className={estyle.maincontentbox}
                id="maincontentboxid"
                style={{ height:"410px" }}
                variants={svgVariants}
                initial="ready"
                animate="start"
            >
                <div className={estyle.routecontainer} id="routecontainerid">
                    <AnimatedRoutes />
                </div>
            </motion.div>
        </div>
    );
};

export default MainContentBox;
