import { motion } from "framer-motion"
import estyle from "./MainNav.module.scss"
import { Link } from "react-router-dom"
import { useState, useContext, useEffect } from "react"
import NavContext from '../../NavContext'

function MainNav({props}) {
    const menudisabledamount = props.disablemenuamount * 1000
    const [enablebutton, setEnableButton] = useState(true)
    const [isPrevActive, setPrevActive] = useState('about')
    const [isActive, setActive] = useState('about');
    let firsttime = true;
    const { changenav } = useContext(NavContext)
    const navlabels = ["ABOUT", "PROJECTS", "CLIENTS", "PARTNERS", "FUTURE", "CONTACT"]
    const thenav = []


    //need to figure out how to animate the unclicked nav item back to it's original colour. right now it just flips back.

    const handleHoverVariant = {
        ready: {color: '#03FFFA'}, /* teal */
        startanim: { color:'#292728',  /* charcoal */ 
            transition: { 
                color:{ delay:1, duration: 2 }
            }
        },
        hoveranim: {
            color:props.hovercolor, /* charcoal */ 
            transition:{ duration:props.hoveranimduration }
        }
    }

    const handleClick = (e, waittime, label) => {

        if(isActive != label)
        {
            setEnableButton(false)

            setTimeout(() => {        
                setEnableButton(true)
            }, menudisabledamount)

            // setTimeout(() => {        
            //     setPrevActive('')
            // }, 3000)

            console.log('isActive is: ' + isActive)
            console.log('isPrevActive is: ' + isPrevActive)
            console.log('-----')
            setPrevActive(isActive)

            setActive(label);
            console.log('setActive has been set to the label')
            console.log('-----')
            console.log('isActive is now: ' + isActive)
            console.log('isPrevActive is now: ' + isPrevActive)
            changenav(e.target.childNodes[0].data.toLowerCase(), waittime)
        }
    }

    useEffect(() => {

        console.log('useEffect - isPrevActive has changed to: ' + isPrevActive)
        let blah = 0;

        if(firsttime == true){
            firsttime = false;
        }
        else
        {
            setTimeout(() => {        
                setPrevActive('')
                console.log('counting to 3 - ' + blah++)
            }, 3000)

            setTimeout(() => {
                console.log('isPrevActive is: ' + isPrevActive)
            }, 3000)
        }


    }, [isPrevActive])

    for(let i = 0; i < navlabels.length; i++) {
        thenav.push(
            <motion.li key={i} className={estyle.mainnavbtn}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay:props.delay + (i*.2), duration: props.duration }}
            >
                {enablebutton ?
                    <Link className={`${estyle.link} ${navlabels[i].toLowerCase()} ${(isActive == navlabels[i].toLowerCase() ?  estyle.selected : null)}`} to={`/${navlabels[i]}`.toLowerCase()} onClick={(e) => handleClick(e, props.sectiondelay, navlabels[i].toLowerCase())}>
                        {isPrevActive == navlabels[i].toLowerCase() ?
                        <motion.div 
                            className={estyle.test}
                            variants={handleHoverVariant}
                            initial="ready"
                            animate="startanim"
                            whileHover={isActive === navlabels[i].toLowerCase() ? "startanim" : "hoveranim"}
                        >
                        {navlabels[i]}
                        </motion.div> :
                        <motion.div
                            className={estyle.link}
                            variants={handleHoverVariant}
                            whileHover={isActive === navlabels[i].toLowerCase() ? "startanim" : "hoveranim"}

                        >
                            {navlabels[i]}
                        </motion.div> 
                        }
                    </Link> :
                    <div className={`${estyle.activelink} ${(isPrevActive == navlabels[i].toLowerCase() ? estyle.selected : null)}`}>{navlabels[i]}</div>
                }
            </motion.li>
        )
    }

    return (
        <nav>
            <ul className={estyle.mainnav}>
                {thenav}
            </ul>
        </nav>
    )
}
export default MainNav;
