    {
        delay:2.6,
        duration1:1,
        duration2:1,
        xposstart:30,
        xposend:12,
        rectwidth:30,
        color:'black'
    }
    let rectangle3props =
    {
        delay:2.65,
        duration1:1,
        duration2:1,
        xposstart:30,
        xposend:12,
        rectwidth:30,
        color:'black'
    }

    let outlineverticalrectprops =
    {
        delay:2.6,
        duration1:1,
        duration2:1,
        color:'black'
    }

    let dotgridprops =
    {
        delay:2.6,
        duration1:1,
        duration2:1,
        color:'black'
    }

    let horizontallineprops =
    {
        delay:2.8,
        duration1:1,
        duration2:1,
        color:'black'
    }

    let rectangle4props =
    {
        delay:2.8,
        duration1:1,
        duration2:1,
        xposstart:0,
        xposend:0,
        rectwidth:8,
        color:'black'
    }

    let verticalrectanglesprops =
    {
        delay:2.7,
        duration1:1,
        duration2:1,
        color:'black'
    }

    let rectanglefillprops =
    {
        delay:2.9,
        duration1:1,
        duration2:1,
        lineduration:1,
        linedelay:4,
        color:'black'
    }

    let greyrectangle1props =
    {
        delay:3.2,
        width:200,
        color:'#CCCCCB'
    }
    let greyrectangle2props =
    {
