import { motion, animate } from "framer-motion"
import estyle from "./Preloader.module.scss"

const Preloader = ({props}) => {

    // const svgVariants = {
    //     ready: { opacity: 0 },
    //     start: { opacity: 1, transition: { delay: props.delay1, duration : props.duration1 }}
    // }

    // const tophalfintro = {
    //     ready: { scale:.8, y:200 },
    //     start: { scale:1, y:0,
    //         transition: {
    //             scale: { duration:props.duration1, type:'tween', ease:'anticipate' },
    //             y:{ delay:props.delay2, duration: props.duration2, type:'tween', ease:'anticipate'}
    //         }}
    // }

    // const bottomhalfintro = {
    //     ready: { scale:.8, y:-200 },
    //     start: { scale:1, y:0, 
    //         transition: {
    //             scale: { duration: props.duration1, type:'tween', ease:'anticipate' },
    //             y:{ delay:props.delay2, duration: props.duration2, type:'tween', ease:'anticipate'}
    //         }}
    // }

    const preloaderOutlineBarAnim = {
        ready: {opacity:0},
        start: { opacity: 1, transition: { delay: props.delay, duration: props.duration, type:'tween', ease:'anticipate' }}
    }

    const preloaderBarAnim = {
        ready: {scaleX:0},
        start: { scaleX: 1,
            transition: { 
                scaleX: { delay: props.delay, duration:props.fillduration, type:'tween', ease:'anticipate' }
            }
        }
    }

    const animateoutdealy = props.delay + props.duration;
    const startanim = async () => {
        // await animate(scope.current, { opacity:1 })
        // await animate("#containerid", { height:"550px", y:-410 }, {duration: .25 })
        // await animate("#pressid", { opacity:1 })
        // await animate("#closeid", { opacity:1 })

        // await animate("#preloaderoutlinebar", {opacity:1}, {delay:props.delay, duration:props.duration, type:'tween', ease:'anticipate'})
        await animate("#preloaderoutlinebar", {opacity:0}, {delay:animateoutdealy, duration:.5, type:'tween', ease:'anticipate'})
    }

    // startanim()

    return (
        <div>
            <motion.div 
                className={estyle.preloaderoutlinebar}
                id="preloaderoutlinebar"
                variants={preloaderOutlineBarAnim}
                style={{originX: 0, opacity:0}}
                posiiton="fixed"
                initial="ready"
                animate="start" 
            >
            </motion.div>

            <motion.div 
            className={estyle.preloaderbar}
            id="preloaderbar"
            variants={preloaderBarAnim}
            style={{originX: 0}}
            posiiton="fixed"
            initial="ready"
            animate="start" 
            >
            </motion.div>
        </div>
    );
};

export default Preloader;
