import { motion } from "framer-motion"
import estyle from "./MainNav.module.scss"

function MainNav({props}) {

    const svgVariants = {
        hidden: { opacity: 0 },
        visible: { opacity: 1, transition: { delay: props.delay, duration : props.duration }}
    }

    const navlabels = ["ABOUT", "PROJECTS", "CLIENTS", "PARTNERS", "FUTURE", "CONTACT"]
    const thenav = []
    for(let i = 0; i < navlabels.length; i++) {
        thenav.push(
            <motion.li key={i} className={estyle.mainnavbtn}
            whileHover={{ color:props.hovercolor }}
            transition={{ duration:props.hoveranimduration}}
            onHoverStart={e => {}}
            onHoverEnd={e => {}}
            >
                <motion.div  
                    // variants={svgVariants}
                    // initial="hidden"
                    // animate="visible"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay:props.delay + (i*.1), duration: props.duration }}
                    /*delay: i*.05*/
                >
                    {navlabels[i]}
                </motion.div>
            </motion.li>
        )
    }

    return (
        <nav>
            <ul className={estyle.mainnav}>
                {thenav}
            </ul>
        </nav>
    )
}
export default MainNav;
