import { motion, useAnimate } from "framer-motion"
import estyle from "./Home.module.scss"
import NavContext from "../../NavContext"
import { useContext, useEffect } from "react"

function Home({props}) {


    console.log('HOME SECTION-')

    //the mapping has to happen before the variants animation so that
    //it can set 'thedelay' number before the variant animation uses it

    let thedelay = 0;
    let theduration = 0;

    const [scope, animate] = useAnimate();
    const { currentnav, homepagevisible } = useContext(NavContext)

    const fadeOut = () => {
        animate(scope.current, { opacity:0 })
    }
    const fadeIn = () => {
        animate(scope.current, { opacity:1 }, {delay:5})
    }

    currentnav.map((navitem) => {
        // if(navitem.navname === "home"){
        // }
        thedelay = navitem.waittime
        theduration = navitem.duration

    })

    useEffect(() => {

        console.log('````````````````` homepagevisible is: ' + homepagevisible)
        if(homepagevisible === false)
        {
            console.log('````````````````` home page should be fading out now ```````````````')
            //fadeOut()
        }
        else
        {
            fadeIn()
        }
    }, [homepagevisible])


    // const svgVariants = {
    //     ready: { opacity:0 },
    //     start: { opacity:0, 
    //         transition: { 
    //             // scaleY: { delay: 2.35, duration: .25, type:'tween', ease:'easeIn' },
    //             // y: { delay:2.35, duration: .25, type: 'tween', ease:'easeIn' },
    //             // scaleX: { delay: 2.6, duration: .25, type:'tween', ease:'backOut' }
    //             //delay has to be the same or greater than the exit duration for other sections
    //             opacity: { delay:2, duration:theduration }
    //         }
    //     },
    //     exit: {opacity:0, transition: {opacity: {duration:theduration}}}
    // }

    return (
        <motion.div
            className={estyle.container}
            ref={scope}
            // variants={svgVariants}
            // initial="ready"
            // animate="start"
            // exit="exit"
        >
            <h1 className={estyle.title}>home</h1>
            <div className={estyle.bgcontainer} />

        </motion.div>
    )
}
export default Home;
