import { motion, useAnimate } from "framer-motion"
import estyle from "./SubModuleContainer.module.scss"
import { useContext, useEffect, useState } from "react";
import SubModulePress from "../sub-module-press/SubModulePress"
import SubModuleTestimonials from "../sub-module-testimonials/SubModuleTestimonials"
import NavContext from "../../../NavContext"

function SubModuleContainer({props}) {

    const [showPress, setShowPress] = useState(false)
    const [showSection, setShowSection] = useState(false)
    const [isCloseButtonClicked, setIsClosedButtonClicked] = useState(false)
    const [scope, animate] = useAnimate();
    const { submodules, changesubmodule, positonstate } = useContext(NavContext)

    let whichmodule = ["1"]
    let whichsection = []
    let theclosebtn = []

    const showPressFunction = (section) => {
        if(section === 'press')
        {
            setShowPress(true)
        }
        else
        {
            setShowPress(false)
        }
    }

    const openState = async () => {
        console.log('openpanel goes here ')
        await animate("#containerid", { opacity:.3, height:"547px", y:-410 }, {duration: .25 })
        await animate("#pressid", { opacity:1 })
        await animate("#closeid", { opacity:1 })
    }

    const closeState = async () => {
        await animate("#pressid", { opacity:0 })
        await animate("#containerid", { height:"137px", y:0 }, {duration:.25 })
    }

    const invisibleState = async () => {
        await animate("#pressid", { opacity:0 })
    }

    // function visibleState() {
    //     animate([
    //         [scope.current, { opacity:1 }]
    //     ])
    // }

    const changeWhatHappens = () => {
        // NNED TO FIGURE THREE SCENARIOS

        /*

            positionstate is at 1 (goes to default state)
            positionstate is at 2 (goes to main content state)
            positionstate is at 3 (goes to sub module state)
            positionstate is at 4 (goes to close button state)

            1. DEFAULT STATE
            home default:
            MC is default home state,
            SM is default state, 
            SMC is invisible default state

            2. MAIN CONTENT STATE
            menu clicked:
            MC is open state,
            SM is collapsed state,
            SMC is invisible state

            3. SUB MODULE STATE
            sub module clicked:
            MC is same state, no effect
            SM is invisible state
            SMC is open state

            4. CLOSE BUTTON STATE
            close button clicked:
            MC is same state, no effect
            SM is open or collapsed state, visible
            SMC is invisible default state  

         */
            submodules.map((item) => {
                switch(item.positionstate) {

                    case 1:
                        //console.log('submodulecontainer - switch - case1')
                        setShowSection(false)
                        closeState()
                        break;
                    case 2:
                        //console.log('submodulecontainer - switch - case2')
                        setShowSection(false)
                        closeState()
                        break;
                    case 3:
                        //console.log('submodulecontainer - switch - case3')
                        setShowSection(true)
                        showPressFunction(item.submodule)
                        openState()
                        break;
                    case 4:
                        //console.log('submodulecontainer - switch - case4')
                        setShowSection(false)
                        closeState()
                        break;
                        default:
                } 
            })
    }

    useEffect(()=>{

        // NEED TO MODIFY THIS BLOCK BELOW SO THAT IT 
        // LISTENS FOR POSITIONSTATE INSTEAD OF YESOPEN
        // 
		// submodules.map((item) => {
        //     if(item.yesopen === true)
        //     {
        //         setShowSection(true)
        //         showPressFunction(item.submodule)
        //         openState()
        //     }
        //     else if(item.yesopen === false)
        //     {
        //         setShowSection(false)
        //         closeState()
        //     }
        // })
        if(isCloseButtonClicked === false)
        {
            changeWhatHappens()
        }

	}, [changesubmodule])

    const handleCloseClick = (e) => {

        // setShowSection(false)
        // closeState()

        // submodules.map((item) => {
        //     console.log('222222222222222222222')
        //     console.log('submodulecontainer - item.positionstate is: ' + item.positionstate)
        //     if(item.positionstate !== null){
        //         console.log('item.positionstate is not null')
        //         console.log('222222222222222222222')
        //         changesubmodule('', false, 2)
        //     }
        // })
        // changesubmodule('', false)
        setIsClosedButtonClicked(true)
        changesubmodule('', false, 4)
        //changeWhatHappens()
    }

    const svgVariants = {
        ready: { opacity: 0 },
        start: { opacity: 1, transition: { delay: props.delay, duration : props.duration }}
    }

    const renderCloseButton = () => {
        theclosebtn = []
            theclosebtn.push
            (  
                <div className={estyle.closebutton}> 
                <motion.div id="closeid" style={{ opacity:0 }} 
                     whileHover={{scale:1.2, color:props.hovercolor, transition: { duration: props.hoverduration }}} 
                     onClick={(e) => handleCloseClick(e)}
                >
                    x
                </motion.div>
                </div>
            )
        // }
    }

    const renderSubModules = () => {
        whichmodule = []
        if(showPress === true){
            whichmodule.push
            (
                <SubModulePress props={{delay:0, duration:1}}/> 
            )
        }
        else if(showPress === false){
            whichmodule.push
            (
                <SubModuleTestimonials props={{delay:0, duration:1}}/>
            )
        }
    }

    const renderSection = () => {
        whichsection = []
        if(showSection === true){
            whichsection.push
            (
                <motion.div id="pressid" style={{opacity:0}}>
                    {theclosebtn}
                    {whichmodule}
                </motion.div>
            )
        }
        else if(showSection === false){
            whichsection.push 
            (
                <motion.div id="pressid" style={{opacity:1}}>
                </motion.div>
            )    
        }
    }

    renderCloseButton()
    renderSubModules()
    renderSection()

    return (
        <div ref={scope}>
            <motion.div id="containerid" className={estyle.container} style={{ height: "137px", y:0 }} variants={svgVariants} initial="ready" animate="start">
                {whichsection}
            </motion.div>
        </div>
    )
}
export default SubModuleContainer;
