import { motion, useAnimate } from "framer-motion"
import estyle from "./SubModuleDefault.module.scss"
import { useContext, useEffect, useState } from "react";
import NavContext from "../../../NavContext"

function SubModuleDefault({props}) {

    const themodule = props.themodule;
    const { currentnav, submodules, changesubmodule } = useContext(NavContext)
    const [isFirstTime, setIsFirstTime] = useState(true)
    const [scope, animate] = useAnimate();
    const whatisit = []

    let amountoftime = .7 * 1000

    const svgVariants = {
        ready: { opacity: 0 },
        start: { opacity: 1, transition: { delay: props.delay, duration : props.duration }}
    }

    function showSubModule() {
        animate([
            [scope.current, { opacity:1 }]
        ]);
    }

    function fadeSubModule() {
        animate([
            [scope.current, { opacity:0 }]
        ]);
    }

    useEffect(()=>{

        submodules.map((item) => {
            console.log('submoduledefault - useeffect - item.yesopen is: ' + item.yesopen)
            if(item.yesopen === true)
            {        
                fadeSubModule()
            }
            else if(item.yesopen === false)
            {
                console.log('set to false 6-66-06-00-')
                if(isFirstTime === true)
                {
                    console.log('firsttime is true 5095900950509059')
                    setIsFirstTime(false)
                }
                else
                {
                    setTimeout(() => {        
                        showSubModule()
                    }, amountoftime)
                }

            }
        })
    }, [changesubmodule])

    useEffect(() => {
        currentnav.map((item) => {
            console.log('item.navname is: ' + item.navname)

            if(item.navname !== 'home')
            {
                fadeSubModule()
            }
        })
    }, [currentnav])

    const handleClick = (e) => {
        console.log('The link was clicked.');
        changesubmodule(e, true)
    }

    if(themodule === '1')
    {
        whatisit.push
        (
            <div key={1} className={estyle.contentarea}>
                <div className={estyle.module1col1}>
                    <div className={estyle.contenttitle}>
                        {props.subtitle}
                    </div>
                    <div className={estyle.contentbody}>
                        {props.subbody}
                    </div>
                    <motion.div className={estyle.contentlink} whileHover={{ color:"#03FFFA" }} onClick={() => {handleClick('testimonials')}}>
                        {props.button}
                    </motion.div>
                </div>   
            </div>
        )    
    }
    else
    {
        whatisit.push
        (
            <div key={2} className={estyle.contentarea}>
                <div className={estyle.module2col1}>
                    <div className={estyle.contentimage}>
                    </div>
                </div>
                <div className={estyle.module2col2}>
                    <div className={estyle.contenttitle}>
                        {props.subtitle}
                    </div>
                    <div className={estyle.contentbody}>
                        {props.subbody}
                    </div>
                    <motion.div className={estyle.contentlink} whileHover={{ color:"#03FFFA" }} onClick={() => {handleClick('press')}}>
                        {props.button}
                    </motion.div>
                </div>   
            </div> 
        )
    }

    return (
        <motion.div className={estyle.container}
            ref={scope}
            variants={svgVariants}
            initial="ready"
            animate="start"
        >
            <div className={estyle.title}>
                {props.title}
            </div>
            <div className={estyle.subline} />
            {whatisit}
        </motion.div>
    )
}
export default SubModuleDefault;
