import { motion, useAnimate, useAnimationControls } from "framer-motion"
import estyle from "./ASquare.module.scss"
import { useEffect, useState } from "react"

function ASquare({props}) {

    console.log(props.id)
    const [showsquare, setShowSquare] = useState(false)

    async function path1animate(){
        await animate("#path1", {opacity:0}, { delay:0, duration:.1})
        await animate("#path1", {opacity:1}, { delay:props.delay, duration:.1})
        await animate("#path1", {opacity:0}, { delay:0, duration:.1})
        await animate("#path1", {opacity:1}, { delay:0, duration:.1})
        await animate("#path1", {opacity:0}, { delay:0, duration:.1})
        await animate("#path1", {opacity:1}, { delay:0, duration:.1})
        await animate("#path1", {opacity:0}, { delay:0, duration:.1})
        await animate("#path1", {opacity:1}, { delay:0, duration:.1})
        await animate("#path1", {opacity:0}, { delay:0, duration:.1})
        await animate("#path1", {opacity:1}, { delay:0, duration:.1})
    }

    const [scope, animate] = useAnimate()

    useEffect(() => {
        path1animate();
    }, [])

    const handleEnter = (e, id) => {
        console.log('ASquare - handleEnter - id is: ' + id)
        setShowSquare(true)

        // submodules.map((item) => {
        //     if(item.positionstate !== null){
        //         changesubmodule(e, true, 3)
        //     }
        // })
    }

    const handleLeave = (e, id) => {
        console.log('ASquare - handleLeave - id is: ' + id)
        setShowSquare(false)
        // submodules.map((item) => {
        //     if(item.positionstate !== null){
        //         changesubmodule(e, true, 3)
        //     }
        // })
    }

    const handleClick = (e, id) => {
        console.log('ASquare - handleClick - id is: ' + id)
        // submodules.map((item) => {
        //     if(item.positionstate !== null){
        //         changesubmodule(e, true, 3)
        //     }
        // })
    }

    return (
        <div ref={scope}>
             {showsquare ?
            <div 
            className={estyle.hoversquare}
            onMouseEnter={() => {handleEnter(props.id)}}
            onMouseLeave={() => {handleLeave(props.id)}}
            >
                hover square
            </div>:
            <div></div>
            }
            <motion.div 
                className={`${estyle.thesquare}`}
                id="path1"
                onMouseEnter={() => {handleEnter(props.id)}}
                onClick={() => {handleClick(props.id)}}
             />
        </div>
    )
}
export default ASquare;
